/**
 * Analytics - Game analytics wrapper using GameAnalytics SDK
 *
 * Free tier: Unlimited events
 * Dashboard: https://gameanalytics.com
 *
 * Setup:
 * 1. Create account at gameanalytics.com
 * 2. Add game, get Game Key and Secret Key
 * 3. Set keys in ANALYTICS_CONFIG below
 */

const ANALYTICS_CONFIG = {
    enabled: false, // Enable when ready for production data
    gameKey: '8ac6be4add5322270364152da68e97ca',
    secretKey: 'cdc6600ca0727e16cc74f30d23c520a07918f04c',
    build: '1.0.0'
};

import { Logger } from '@core/Logger';
import { Registry } from '@core/Registry';

const Analytics = {
    initialized: false,

    /**
     * Initialize analytics (called once on game start)
     */
    init() {
        if (!ANALYTICS_CONFIG.enabled) {
            Logger.info('[Analytics] Disabled - set keys in AnalyticsManager.js');
            return;
        }

        // Load GameAnalytics SDK
        if (!gameanalytics) {
            Logger.warn('[Analytics] GameAnalytics SDK not loaded');
            return;
        }

        try {
            gameanalytics.GameAnalytics.configureBuild(ANALYTICS_CONFIG.build);
            gameanalytics.GameAnalytics.initialize(
                ANALYTICS_CONFIG.gameKey,
                ANALYTICS_CONFIG.secretKey
            );
            this.initialized = true;
            Logger.info('[Analytics] Initialized');
        } catch (e) {
            Logger.error('[Analytics] Failed to initialize:', e);
        }
    },

    /**
     * Track game progression (level complete, boss killed, etc.)
     */
    trackProgression(status: string, area: string, step: string | null = null) {
        if (!this.initialized) return;

        // status: 'Start', 'Complete', 'Fail'
        // area: 'Zone_Grasslands', 'Boss_Alpha', etc.
        try {
            if (step) {
                gameanalytics.GameAnalytics.addProgressionEvent(
                    gameanalytics.EGAProgressionStatus[status],
                    area,
                    step
                );
            } else {
                gameanalytics.GameAnalytics.addProgressionEvent(
                    gameanalytics.EGAProgressionStatus[status],
                    area
                );
            }
        } catch (_e) {
            /* ignore */
        }
    },

    /**
     * Track resource/economy events (gold earned, items crafted)
     */
    trackResource(flowType: string, currency: string, amount: number, itemType: string, itemId: string) {
        if (!this.initialized) return;

        // flowType: 'Source' (earned) or 'Sink' (spent)
        // currency: 'gold', 'gems', etc.
        try {
            gameanalytics.GameAnalytics.addResourceEvent(
                gameanalytics.EGAResourceFlowType[flowType],
                currency,
                amount,
                itemType,
                itemId
            );
        } catch (_e) {
            /* ignore */
        }
    },

    /**
     * Track custom design events
     */
    trackEvent(eventId: string, value: number | null = null) {
        if (!this.initialized) return;

        try {
            if (value !== null) {
                gameanalytics.GameAnalytics.addDesignEvent(eventId, value);
            } else {
                gameanalytics.GameAnalytics.addDesignEvent(eventId);
            }
        } catch (_e) {
            /* ignore */
        }
    },

    /**
     * Track errors
     */
    trackError(severity: string, message: string) {
        if (!this.initialized) return;

        // severity: 'Debug', 'Info', 'Warning', 'Error', 'Critical'
        try {
            gameanalytics.GameAnalytics.addErrorEvent(
                gameanalytics.EGAErrorSeverity[severity],
                message
            );
        } catch (_e) {
            /* ignore */
        }
    }
};

if (Registry) Registry.register('Analytics', Analytics);

// ES6 Module Export
export { Analytics };

/**
 * GameRenderer - Canvas rendering system with viewport
 *
 * Mobile shows a cropped view, PC shows more of the world
 *
 * Owner: Director
 */

import { Logger } from './Logger';
import { Registry } from './Registry';
import { getWorldWidth, getWorldHeight } from './GameRendererWorldSize';
import { renderGameLayers } from './GameRendererLayers';
import {
    updateViewport as updateViewportImpl,
    updateCamera as updateCameraImpl,
    resizeCanvas
} from './GameRendererViewport';
import { ShadowRenderer } from '../rendering/ShadowRenderer';
import { EntityRenderService } from '../rendering/EntityRenderService';
import { RenderProfiler, RenderTiming } from '../rendering/RenderProfiler';
import { GridRenderer } from '../rendering/GridRenderer';
import { DebugOverlays } from '../rendering/DebugOverlays';
import { HomeOutpostRenderer } from '../rendering/HomeOutpostRenderer';
import { DOMUtils } from './DOMUtils';
import { IGame, IEntity, ISystem } from '../types/core';

// Import specific system types
import { PlatformManager } from './PlatformManager';
import { VFXController } from '../vfx/VFXController';
import { CollisionSystem } from '../systems/CollisionSystem';
import { WorldRenderer } from '../rendering/WorldRenderer';
import { RoadRenderer } from '../rendering/RoadRenderer';
import { HeroRenderer } from '../rendering/HeroRenderer';
import { DinosaurRenderer } from '../rendering/DinosaurRenderer';
import { ResourceRenderer } from '../rendering/ResourceRenderer';
import { AmbientSystem } from '../world/AmbientSystem';
import { FogOfWarSystem } from '../vfx/FogOfWarSystem';
import { EnvironmentRenderer } from '../rendering/EnvironmentRenderer';
import { LightingSystem } from '../vfx/LightingSystem';
import { HomeBase } from '../world/HomeBase';

const GameRenderer = {
    canvas: null as HTMLCanvasElement | null,
    ctx: null as CanvasRenderingContext2D | null,
    hero: null as IEntity | null,
    debugMode: false,
    gridMode: false, // Separate toggle for grid overlay
    game: null as IGame | null,
    shadowCanvas: null as HTMLCanvasElement | null,
    shadowCtx: null as CanvasRenderingContext2D | null,
    _worldRenderer: null as WorldRenderer | null,
    _roadRenderer: null as RoadRenderer | null,
    _vfxController: null as typeof VFXController | null,
    _homeBase: null as typeof HomeBase | null,
    _heroRenderer: null as typeof HeroRenderer | null,
    _dinosaurRenderer: null as typeof DinosaurRenderer | null,
    _resourceRenderer: null as typeof ResourceRenderer | null,
    _ambientSystem: null as typeof AmbientSystem | null,
    _fogSystem: null as typeof FogOfWarSystem | null,
    _envRenderer: null as EnvironmentRenderer | null,
    _lightingSystem: null as typeof LightingSystem | null,
    _renderTiming: null as RenderTiming | null, // Profiler data container

    get worldWidth() {
        return getWorldWidth();
    },
    get worldHeight() {
        return getWorldHeight();
    },

    // Viewport (what portion of the world is visible)
    viewport: {
        x: 0,
        y: 0,
        width: 450, // Mobile default (9:16 aspect)
        height: 800
    },

    // GC Optimization: Pre-allocated array for Y-sorting
    _sortableEntities: [] as IEntity[],

    /**
     * Initialize the renderer
     */
    init(game: IGame) {
        this.game = game;
        this.canvas = document.getElementById('game-canvas') as HTMLCanvasElement;
        if (!this.canvas) {
            Logger.error('[GameRenderer] Canvas not found');
            return false;
        }

        this.ctx = this.canvas.getContext('2d');

        // Create Shadow Buffer (Offscreen Canvas)
        this.shadowCanvas = DOMUtils.createCanvas();
        this.shadowCtx = this.shadowCanvas.getContext('2d');
        // Initial size sync
        this.resizeShadowBuffer();

        // Listen for platform changes
        const platformManager = this.game ? this.game.getSystem<typeof PlatformManager>('PlatformManager') : null;
        if (platformManager) {
            platformManager.on('modechange', () => this.updateViewport());
            this.updateViewport();
        } else {
            this.resize();
        }

        // Handle window resize
        addEventListener('resize', () => {
            if (this.game) {
                const pm = this.game.getSystem<typeof PlatformManager>('PlatformManager');
                if (pm && pm.isMobile()) {
                    this.updateViewport(); // Recalculate aspect ratio on mobile
                } else {
                    this.resize();
                }
            }
        });

        // GC Optimization: Cache system references for render loop
        this._worldRenderer = this.game.getSystem<WorldRenderer>('WorldRenderer');
        this._roadRenderer = this.game.getSystem<RoadRenderer>('RoadRenderer');
        this._vfxController = this.game.getSystem<typeof VFXController>('VFXController');
        this._homeBase = this.game.getSystem<typeof HomeBase>('HomeBase');
        this._heroRenderer = this.game.getSystem<typeof HeroRenderer>('HeroRenderer');
        this._dinosaurRenderer = this.game.getSystem<typeof DinosaurRenderer>('DinosaurRenderer');
        this._resourceRenderer = this.game.getSystem<typeof ResourceRenderer>('ResourceRenderer');
        this._ambientSystem = this.game.getSystem<typeof AmbientSystem>('AmbientSystem');
        this._fogSystem = this.game.getSystem<typeof FogOfWarSystem>('FogOfWarSystem');
        this._envRenderer = this.game.getSystem<EnvironmentRenderer>('EnvironmentRenderer');
        this._lightingSystem = this.game.getSystem<typeof LightingSystem>('LightingSystem');

        Logger.info('[GameRenderer] Initialized');
        return true;
    },

    resizeShadowBuffer() {
        if (this.shadowCanvas && this.canvas) {
            this.shadowCanvas.width = this.canvas.width;
            this.shadowCanvas.height = this.canvas.height;
        }
    },

    updateViewport() {
        updateViewportImpl({
            canvas: this.canvas!,
            viewport: this.viewport,
            worldWidth: this.worldWidth,
            worldHeight: this.worldHeight,
            hero: this.hero
        });
        this.resize();
        if (this.ctx) this.render();
    },

    updateCamera() {
        updateCameraImpl({
            canvas: this.canvas!,
            viewport: this.viewport,
            worldWidth: this.worldWidth,
            worldHeight: this.worldHeight,
            hero: this.hero
        });
    },

    /**
     * Set the hero reference
     * @param {Hero} hero
     */
    setHero(hero: IEntity) {
        this.hero = hero;
    },

    /**
     * Convert world coordinates to screen coordinates
     */
    worldToScreen(worldX: number, worldY: number) {
        return {
            x: worldX - this.viewport.x,
            y: worldY - this.viewport.y
        };
    },

    /**
     * Get the visible world bounds
     */
    getVisibleBounds() {
        return {
            left: this.viewport.x,
            top: this.viewport.y,
            right: this.viewport.x + this.viewport.width,
            bottom: this.viewport.y + this.viewport.height,
            width: this.viewport.width,
            height: this.viewport.height
        };
    },

    resize() {
        resizeCanvas({ canvas: this.canvas!, viewport: this.viewport });
        this.resizeShadowBuffer();
    },

    /**
     * Render the composite shadow pass (delegates to ShadowRenderer)
     */
    simpleShadows: false,

    renderShadowPass(entities: IEntity[]) {
        if (ShadowRenderer) {
            ShadowRenderer.simpleShadows = this.simpleShadows;
            ShadowRenderer.renderShadowPass(
                this.ctx,
                entities,
                this.viewport,
                {
                    heroRenderer: this._heroRenderer,
                    dinosaurRenderer: this._dinosaurRenderer,
                    resourceRenderer: this._resourceRenderer
                },
                this._renderTiming
            );
        }
    },

    /**
     * Fast simple ellipse shadows (delegates to ShadowRenderer)
     */
    renderSimpleShadows(entities: IEntity[]) {
        if (ShadowRenderer) {
            ShadowRenderer.renderSimpleShadows(this.ctx, entities, this.viewport);
        }
    },

    /**
     * Clear and render all entities
     */
    render(alpha = 1) {
        if (!this.ctx) return;

        this.updateCamera();

        renderGameLayers(
            {
                ctx: this.ctx,
                canvas: this.canvas,
                viewport: this.viewport,
                worldWidth: this.worldWidth,
                worldHeight: this.worldHeight,
                hero: this.hero,
                game: this.game,
                debugMode: this.debugMode,
                gridMode: this.gridMode,
                simpleShadows: this.simpleShadows,
                _worldRenderer: this._worldRenderer,
                _roadRenderer: this._roadRenderer,
                _vfxController: this._vfxController,
                _homeBase: this._homeBase,
                _heroRenderer: this._heroRenderer,
                _dinosaurRenderer: this._dinosaurRenderer,
                _resourceRenderer: this._resourceRenderer,
                _ambientSystem: this._ambientSystem,
                _fogSystem: this._fogSystem,
                _envRenderer: this._envRenderer,
                _lightingSystem: this._lightingSystem,
                _renderTiming: this._renderTiming,
                getVisibleBounds: () => this.getVisibleBounds(),
                renderShadowPass: (e) => this.renderShadowPass(e)
            },
            alpha
        );
    },

    /**
     * Start detailed render profiling (delegates to RenderProfiler)
     */
    startRenderProfile() {
        if (RenderProfiler) {
            this._renderTiming = RenderProfiler.start();
        }
    },

    /**
     * Stop render profiling and print results (delegates to RenderProfiler)
     */
    stopRenderProfile() {
        if (RenderProfiler) {
            RenderProfiler.stop();
        }
        this._renderTiming = null;
    },

    drawGrid() {
        GridRenderer?.drawGrid(this.ctx, this.viewport, this.canvas, this.game);
    },
    toggleDebug() {
        this.debugMode = !this.debugMode;
        Logger.info(`[GameRenderer] Debug mode: ${this.debugMode}`);
        return this.debugMode;
    },
    toggleGrid() {
        this.gridMode = !this.gridMode;
        Logger.info(`[GameRenderer] Grid mode: ${this.gridMode}`);
        return this.gridMode;
    },
    drawWorldBoundary() {
        DebugOverlays?.drawWorldBoundary(this.ctx, this.viewport, this.worldWidth, this.worldHeight, this.game);
    },
    drawDebugGrid() {
        DebugOverlays?.drawDebugGrid(this.ctx, this.getVisibleBounds());
    },
    drawHomeOutpost() {
        HomeOutpostRenderer?.draw(this.ctx, this.worldWidth, this.worldHeight, this.game);
    }
};

// Export
if (Registry) Registry.register('GameRenderer', GameRenderer);

// ES6 Module Export
export { GameRenderer };

/**
 * GameRendererLayers - Render pass execution for GameRenderer
 */
import { Logger } from './Logger';
import { ShadowRenderer } from '../rendering/ShadowRenderer';
import { EntityRenderService } from '../rendering/EntityRenderService';
import { DebugOverlays } from '../rendering/DebugOverlays';
import { CollisionSystem } from '../systems/CollisionSystem';
import type { IEntity, IGame } from '../types/core';
import type { RenderTiming } from '../rendering/RenderProfiler';
import type { WorldRenderer } from '../rendering/WorldRenderer';
import type { RoadRenderer } from '../rendering/RoadRenderer';
import type { HeroRenderer } from '../rendering/HeroRenderer';
import type { DinosaurRenderer } from '../rendering/DinosaurRenderer';
import type { ResourceRenderer } from '../rendering/ResourceRenderer';
import type { EnvironmentRenderer } from '../rendering/EnvironmentRenderer';

export interface GameRendererState {
    ctx: CanvasRenderingContext2D;
    canvas: HTMLCanvasElement;
    viewport: { x: number; y: number; width: number; height: number };
    worldWidth: number;
    worldHeight: number;
    hero: IEntity | null;
    game: IGame | null;
    debugMode: boolean;
    gridMode: boolean;
    simpleShadows: boolean;
    _worldRenderer: WorldRenderer | null;
    _roadRenderer: RoadRenderer | null;
    _vfxController: { bgParticles?: { render: (ctx: CanvasRenderingContext2D) => void }; render?: (ctx: CanvasRenderingContext2D) => void } | null;
    _homeBase: { render: (ctx: CanvasRenderingContext2D) => void } | null;
    _heroRenderer: HeroRenderer | null;
    _dinosaurRenderer: DinosaurRenderer | null;
    _resourceRenderer: ResourceRenderer | null;
    _ambientSystem: { render: (ctx: CanvasRenderingContext2D) => void } | null;
    _fogSystem: { render: (ctx: CanvasRenderingContext2D, viewport: unknown) => void } | null;
    _envRenderer: EnvironmentRenderer | null;
    _lightingSystem: { render: (ctx: CanvasRenderingContext2D) => void } | null;
    _renderTiming: RenderTiming | null;
    getVisibleBounds: () => { left: number; top: number; right: number; bottom: number };
    renderShadowPass: (entities: IEntity[]) => void;
}

export function renderGameLayers(state: GameRendererState, alpha: number): void {
    const {
        ctx,
        canvas,
        viewport,
        worldWidth,
        worldHeight,
        debugMode,
        gridMode,
        game,
        _worldRenderer,
        _roadRenderer,
        _vfxController,
        _homeBase,
        _heroRenderer,
        _dinosaurRenderer,
        _resourceRenderer,
        _ambientSystem,
        _fogSystem,
        _envRenderer,
        _lightingSystem,
        _renderTiming,
        getVisibleBounds,
        renderShadowPass
    } = state;

    const timing = _renderTiming;
    if (timing) timing.frames++;

    let t0: number;

    if (timing) t0 = performance.now();
    if (_worldRenderer) {
        _worldRenderer.render(ctx, viewport);
    } else {
        ctx.fillStyle = '#000';
        ctx.fillRect(0, 0, canvas.width, canvas.height);
    }
    if (timing) timing.world += performance.now() - t0;

    if (timing) t0 = performance.now();
    if (_roadRenderer) {
        ctx.save();
        ctx.translate(-viewport.x, -viewport.y);
        _roadRenderer.render(ctx, viewport);
        ctx.restore();
    }
    if (timing) timing.roads = (timing.roads || 0) + performance.now() - t0;

    if (timing) t0 = performance.now();
    if (_vfxController?.bgParticles) {
        ctx.save();
        ctx.translate(-viewport.x, -viewport.y);
        _vfxController.bgParticles.render(ctx);
        ctx.restore();
    }
    if (timing) timing.vfxBg += performance.now() - t0;

    if (timing) t0 = performance.now();
    const sortableEntities = EntityRenderService
        ? EntityRenderService.collectAndSort(getVisibleBounds())
        : [];
    if (timing) timing.entitySort += performance.now() - t0;

    if (timing) t0 = performance.now();
    renderShadowPass(sortableEntities);
    if (timing) timing.shadows += performance.now() - t0;

    if (timing) t0 = performance.now();
    ctx.save();
    ctx.translate(-viewport.x, -viewport.y);

    let tSub: number;
    if (timing) tSub = performance.now();
    if (_homeBase) _homeBase.render(ctx);
    if (timing) timing.entHomeBase = (timing.entHomeBase || 0) + performance.now() - tSub;

    if (EntityRenderService) {
        const renderers = {
            hero: state.hero,
            heroRenderer: _heroRenderer,
            dinosaurRenderer: _dinosaurRenderer,
            resourceRenderer: _resourceRenderer
        };
        EntityRenderService.renderAll(ctx, sortableEntities, renderers, timing, alpha);
        EntityRenderService.renderUIOverlays(ctx, sortableEntities, timing);
    }

    ctx.restore();
    if (timing) timing.entities += performance.now() - t0;

    if (timing) t0 = performance.now();
    if (_ambientSystem) {
        ctx.save();
        ctx.translate(-viewport.x, -viewport.y);
        _ambientSystem.render(ctx);
        ctx.restore();
    }
    if (timing) timing.ambient += performance.now() - t0;

    if (timing) t0 = performance.now();
    if (_fogSystem) _fogSystem.render(ctx, viewport);
    if (timing) timing.fog += performance.now() - t0;

    if (timing) t0 = performance.now();
    if (_vfxController?.render) {
        ctx.save();
        ctx.translate(-viewport.x, -viewport.y);
        _vfxController.render(ctx);
        ctx.restore();
    }
    if (timing) timing.vfxFg += performance.now() - t0;

    if (timing) t0 = performance.now();
    if (_envRenderer) _envRenderer.render(ctx, viewport);
    if (timing) timing.envOverlay += performance.now() - t0;

    if (timing) t0 = performance.now();
    if (_lightingSystem?.render) {
        try {
            ctx.save();
            ctx.translate(-viewport.x, -viewport.y);
            _lightingSystem.render(ctx);
            ctx.restore();
        } catch (e) {
            ctx.restore();
            Logger.warn('[GameRenderer] LightingSystem render error:', (e as Error).message);
        }
    }
    if (timing) timing.lighting = (timing.lighting || 0) + performance.now() - t0;

    if (debugMode && DebugOverlays) {
        DebugOverlays.drawWorldBoundary(ctx, viewport, worldWidth, worldHeight, game);
    }

    const collisionSystem = game?.getSystem<CollisionSystem>('CollisionSystem');
    if (collisionSystem?.renderDebug) {
        ctx.save();
        ctx.translate(-viewport.x, -viewport.y);
        collisionSystem.renderDebug(ctx);
        ctx.restore();
    }

    if (gridMode && DebugOverlays) {
        ctx.save();
        ctx.translate(-viewport.x, -viewport.y);
        DebugOverlays.drawDebugGrid(ctx, getVisibleBounds());
        ctx.restore();
    }
}

/**
 * GameState - Global Reactive Store
 *
 * Centralizes transient game data (Inventory, Gold, Unlocks).
 * Decouples UI from Entities.
 */

import { Logger } from './Logger';
import { EventBus } from './EventBus';
import { GameConstants, getConfig } from '@data/GameConstants';
import { Registry } from './Registry';

interface IGameStateData {
    gold: number;
    inventory: Record<string, number>;
    unlocks: string[];
    questId: string | null;
    questProgress: number;
}

class GameState {
    data: IGameStateData;

    constructor() {
        this.data = {
            gold: 0,
            inventory: {},
            unlocks: [],
            questId: null,
            questProgress: 0
        };

        Logger.info('[GameState] Initialized');
    }

    /**
     * Initialize state with defaults
     * @param {Partial<IGameStateData> | object} config - Configuration object
     */
    init(config: Partial<IGameStateData> | { constructor?: { name: string } } = {}) {
        // Handle SystemLoader calling init(game)
        // If config is the Game instance, treat as empty config
        if ('constructor' in config && config.constructor && config.constructor.name === 'Game') {
            config = {};
        }

        const safeConfig = config as Partial<IGameStateData>;

        // Load defaults from GameConstants if not provided
        const defaultGold =
            GameConstants && GameConstants.Core ? GameConstants.Core.INITIAL_GOLD : 0;

        const defaults: Partial<IGameStateData> = {
            gold: defaultGold,
            ...safeConfig
        };

        this.data = { ...this.data, ...defaults };
        Logger.info('[GameState] State initialized. Gold:', this.data.gold);
    }

    /**
     * Get a value
     * @param {K} key
     */
    get<K extends keyof IGameStateData>(key: K): IGameStateData[K] {
        return this.data[key];
    }

    /**
     * Set a value and emit change
     * @param {K} key
     * @param {IGameStateData[K]} value
     */
    set<K extends keyof IGameStateData>(key: K, value: IGameStateData[K]) {
        this.data[key] = value;
        this.emitChange(key, value);
    }

    /**
     * Update inventory and emit specific inventory event
     * @param {string} itemId
     * @param {number} qty
     */
    updateInventory(itemId: string, qty: number) {
        this.data.inventory[itemId] = qty;

        if (EventBus) {
            EventBus.emit('INVENTORY_UPDATED', this.data.inventory);
        }
    }

    emitChange<K extends keyof IGameStateData>(key: K, value: IGameStateData[K]) {
        if (EventBus) {
            EventBus.emit('STATE_CHANGED', { key, value });
            // Specific events for convenience
            if (key === 'gold') EventBus.emit('GOLD_CHANGED', value);
        }
    }
}

// Create singleton and export
const gameState = new GameState();
if (Registry) Registry.register('GameState', gameState);

export { GameState, gameState };

/**
 * Performance Profiler - In-game FPS and memory stats
 *
 * Shows real-time performance metrics when enabled.
 * Toggle with F3 key or ENV.SHOW_FPS.
 */

import { Logger } from './Logger';
import { entityManager } from './EntityManager';
import { VFXController } from '@vfx/VFXController';
import { WorldManager } from '../world/WorldManager';
import { GameInstance } from './Game';
import { Registry } from './Registry';
import { DOMUtils } from './DOMUtils';

// Chrome/Edge Helper for memory stats
interface PerformanceMemory {
    usedJSHeapSize: number;
    totalJSHeapSize: number;
    jsHeapSizeLimit: number;
}

interface ProfilerEnv {
    SHOW_FPS?: boolean;
    DEBUG?: boolean;
}

// ENV fallback for runtime flags (in production this would be injected)
const ENV: ProfilerEnv = {
    SHOW_FPS: false,
    DEBUG: false
};

const Profiler = {
    enabled: false,
    element: null as HTMLElement | null,

    // Metrics
    fps: 0,
    frameTime: 0,
    deltaTime: 0,
    memoryUsed: 0,
    entityCount: 0,
    vfxCount: 0,
    currentZone: 'N/A',
    heroPos: { x: 0, y: 0 },

    // Internals
    frames: 0,
    lastTime: performance.now(),
    lastFrameTime: performance.now(),

    /**
     * Initialize profiler display
     */
    init() {
        // Check if should auto-enable
        this.enabled = ENV.SHOW_FPS || false;

        // Create display element
        this.element = DOMUtils.create('div', {
            id: 'profiler',
            cssText: `
            position: fixed;
            top: 50%;
            right: calc(50% + 520px);
            transform: translateY(-50%);
            background: rgba(0, 0, 0, 0.9);
            color: #fff;
            font-family: 'Consolas', monospace;
            font-size: 24px;
            padding: 16px 20px;
            border-radius: 8px;
            border: 1px solid #444;
            z-index: 99999;
            pointer-events: none;
            display: ${this.enabled ? 'block' : 'none'};
            line-height: 1.5;
        `
        });
        document.body.appendChild(this.element);

        // Toggle with F3
        document.addEventListener('keydown', (e) => {
            if (e.key === 'F3') {
                this.toggle();
                e.preventDefault();
            }
        });

        Logger.info('[Profiler] Initialized (F3 to toggle)');
    },

    /**
     * Toggle visibility
     */
    toggle() {
        if (!this.element) return;
        this.enabled = !this.enabled;
        this.element.style.display = this.enabled ? 'block' : 'none';
    },

    /**
     * Call each frame
     */
    update() {
        if (!this.element) return;
        const now = performance.now();
        this.deltaTime = now - this.lastFrameTime;
        this.lastFrameTime = now;

        if (!this.enabled) return;

        this.frames++;
        const delta = now - this.lastTime;

        // Update every 500ms
        if (delta >= 500) {
            this.fps = Math.round((this.frames * 1000) / delta);
            this.frameTime = parseFloat((delta / this.frames).toFixed(1));
            this.frames = 0;
            this.lastTime = now;

            // Get memory if available
            const memory = (performance as unknown as { memory?: PerformanceMemory }).memory;
            if (memory) {
                this.memoryUsed = Math.round(memory.usedJSHeapSize / 1048576);
            }

            // Get entity count
            if (entityManager) {
                this.entityCount = entityManager.getAll().length;
            }

            // Get VFX/particle count
            if (VFXController && VFXController.getActiveCount) {
                this.vfxCount = VFXController.getActiveCount();
            }

            // Get current zone
            if (WorldManager && GameInstance?.hero) {
                const hero = GameInstance.hero;
                const island = WorldManager.getIslandAt?.(hero.x, hero.y);
                this.currentZone = island?.name || 'Unknown';
            }

            // Get hero position
            if (GameInstance?.hero) {
                this.heroPos.x = Math.round(GameInstance.hero.x);
                this.heroPos.y = Math.round(GameInstance.hero.y);
            }

            this.render();
        }
    },

    /**
     * Render stats
     */
    render() {
        const fpsColor = this.fps >= 55 ? '#4f4' : this.fps >= 30 ? '#ff4' : '#f44';

        let html = `<div style="margin-bottom:4px"><b>PROFILER</b> <span style="font-size:10px;opacity:0.6">(F3)</span></div>`;
        html += `<div style="color:${fpsColor}">FPS: ${this.fps}</div>`;
        html += `<div>Frame: ${this.frameTime}ms</div>`;
        html += `<div>Delta: ${this.deltaTime.toFixed(1)}ms</div>`;

        if (this.memoryUsed > 0) {
            html += `<div>Memory: ${this.memoryUsed}MB</div>`;
        }

        html += `<div style="margin-top:8px;border-top:1px solid #444;padding-top:8px">`;
        html += `<div>Entities: ${this.entityCount}</div>`;
        html += `<div>VFX: ${this.vfxCount}</div>`;
        html += `</div>`;

        html += `<div style="margin-top:8px;border-top:1px solid #444;padding-top:8px">`;
        html += `<div>Zone: ${this.currentZone}</div>`;
        html += `<div>Pos: ${this.heroPos.x}, ${this.heroPos.y}</div>`;
        html += `</div>`;

        this.element.innerHTML = html;
    }
};

// Self-updating loop (no need to call from game loop)
function profilerLoop() {
    Profiler.update();
    requestAnimationFrame(profilerLoop);
}

// Auto-init when DOM ready
if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', () => {
        Profiler.init();
        profilerLoop();
    });
} else {
    Profiler.init();
    profilerLoop();
}

