/**
 * EntityRenderService - Handles entity collection, Y-sorting, and render dispatch
 *
 * Extracted from GameRenderer to reduce file size and improve maintainability.
 * Owner: EntityRenderService
 */

import { entityManager } from '@core/EntityManager';
import { Registry } from '@core/Registry';
import { EntityTypes } from '@config/EntityTypes';
import { IEntity, IViewport } from '@app-types/core';
import { RenderTiming } from './RenderProfiler';
import { RendererCollection } from '../types/rendering';
import { isRenderable } from '../utils/typeGuards';

interface IVisibleBounds {
    left: number;
    top: number;
    right: number;
    bottom: number;
    width: number;
    height: number;
}

// Unmapped modules - need manual import

const EntityRenderService = {
    // GC Optimization: Pre-allocated array for Y-sorting
    _sortableEntities: [] as IEntity[],

    /**
     * Collect visible entities from entityManager and Y-sort them
     * @param {object} visibleBounds - {left, top, right, bottom, width, height}
     * @returns {array} Sorted array of active entities
     */
    collectAndSort(visibleBounds: IVisibleBounds) {
        const sortableEntities = this._sortableEntities;
        sortableEntities.length = 0; // Clear without deallocation

        if (!entityManager) return sortableEntities;

        // Add padding to prevent culling objects partially on screen
        const bounds = {
            x: visibleBounds.left - 200,
            y: visibleBounds.top - 200,
            width: visibleBounds.width + 400,
            height: visibleBounds.height + 400
        };

        const allEntities = entityManager.queryRect(bounds);

        // Filter active entities
        for (const e of allEntities) {
            if (e.active) sortableEntities.push(e);
        }

        // Helper to determine render layer priority
        const getRenderLayer = (e: IEntity) => {
            if (e.entityType === EntityTypes.DROPPED_ITEM) return 2; // Top priority
            if (e.entityType === EntityTypes.HERO) return 1; // Middle priority (above nodes)
            return 0; // Standard (nodes, enemies, etc.)
        };

        sortableEntities.sort((a, b) => {
            const layerA = getRenderLayer(a);
            const layerB = getRenderLayer(b);

            // Primary Sort: Layer Priority
            if (layerA !== layerB) {
                return layerA - layerB;
            }

            // Secondary Sort: Y Position (within same layer)
            const ay = a.y + (a.height ? a.height / 2 : 0);
            const by = b.y + (b.height ? b.height / 2 : 0);

            // Stable sort for equal Y
            if (Math.abs(ay - by) < 0.1) {
                return a.id > b.id ? 1 : -1;
            }
            return ay - by;
        });

        return sortableEntities;
    },

    /**
     * Render a single entity using appropriate renderer
     * @param {CanvasRenderingContext2D} ctx
     * @param {object} entity
     * @param {object} renderers - {hero, heroRenderer, dinosaurRenderer, resourceRenderer}
     * @param {object} timing - Optional timing object for profiling
     * @param {number} alpha - Interpolation factor
     */
    renderEntity(ctx: CanvasRenderingContext2D, entity: IEntity, renderers: RendererCollection, timing: RenderTiming | null = null, alpha = 1) {
        const tSub = timing ? performance.now() : 0;
        const type = entity.entityType;

        // Pass 'false' for includeShadow to prevent double rendering
        if (entity === renderers.hero) {
            if (renderers.heroRenderer) {
                renderers.heroRenderer.render(ctx, renderers.hero, false, alpha);
            } else if (isRenderable(entity)) {
                entity.render(ctx);
            }
            if (timing) timing.entHeroTime = (timing.entHeroTime || 0) + performance.now() - tSub;
        } else if (type === EntityTypes.DINOSAUR && renderers.dinosaurRenderer) {
            renderers.dinosaurRenderer.render(ctx, entity, false, alpha);
            if (timing) timing.entDinoTime = (timing.entDinoTime || 0) + performance.now() - tSub;
        } else if (type === EntityTypes.RESOURCE && renderers.resourceRenderer) {
            renderers.resourceRenderer.render(ctx, entity, false);
            if (timing) timing.entResTime = (timing.entResTime || 0) + performance.now() - tSub;
        } else if (type === EntityTypes.MERCHANT) {
            if (isRenderable(entity)) entity.render(ctx);
            if (timing) {
                timing.entMerchantTime = (timing.entMerchantTime || 0) + performance.now() - tSub;
                timing.entMerchantCount = (timing.entMerchantCount || 0) + 1;
            }
        } else if (type === EntityTypes.DROPPED_ITEM) {
            if (isRenderable(entity)) entity.render(ctx);
            if (timing) {
                timing.entDroppedTime = (timing.entDroppedTime || 0) + performance.now() - tSub;
                timing.entDroppedCount = (timing.entDroppedCount || 0) + 1;
            }
        } else {
            if (isRenderable(entity)) entity.render(ctx);
            if (timing) {
                timing.entOtherTime = (timing.entOtherTime || 0) + performance.now() - tSub;
                const typeName = type || (entity.constructor?.name) || 'unknown';
                timing.entOtherTypes = timing.entOtherTypes || {};
                timing.entOtherTypes[typeName] = (timing.entOtherTypes[typeName] || 0) + 1;
            }
        }

        if (timing) timing.entCount = (timing.entCount || 0) + 1;
    },

    /**
     * Render all entities in the sorted collection
     * @param {CanvasRenderingContext2D} ctx
     * @param {array} entities - Y-sorted entity array
     * @param {object} renderers - Renderer references
     * @param {object} timing - Optional profiling object
     * @param {number} alpha - Interpolation factor (0-1)
     */
    renderAll(ctx: CanvasRenderingContext2D, entities: IEntity[], renderers: RendererCollection, timing: RenderTiming | null = null, alpha = 1) {
        // Initialize timing counters
        if (timing) {
            timing.entHeroTime = timing.entHeroTime || 0;
            timing.entDinoTime = timing.entDinoTime || 0;
            timing.entResTime = timing.entResTime || 0;
            timing.entOtherTime = timing.entOtherTime || 0;
            timing.entCount = timing.entCount || 0;
        }

        for (const entity of entities) {
            this.renderEntity(ctx, entity, renderers, timing, alpha);
        }
    },

    /**
     * Render UI overlays (health bars) for all entities
     * @param {CanvasRenderingContext2D} ctx
     * @param {array} entities
     * @param {object} timing
     */
    renderUIOverlays(ctx: CanvasRenderingContext2D, entities: IEntity[], timing: RenderTiming | null = null) {
        const tSub = timing ? performance.now() : 0;

        for (const entity of entities) {
            if (entity.renderUI) {
                entity.renderUI(ctx);
            }
        }

        if (timing) timing.entUITime = (timing.entUITime || 0) + performance.now() - tSub;
    }
};

if (Registry) Registry.register('EntityRenderService', EntityRenderService);

export { EntityRenderService };

/**
 * HeroRenderer - Dedicated rendering system for the player character
 *
 * Extracted from Hero.js to separate logic from presentation.
 * Uses RenderConfig for constants.
 */

import { Logger } from '@core/Logger';
import { RenderConfig } from '@config/RenderConfig';
import { MaterialLibrary } from '@vfx/MaterialLibrary';
import { AssetLoader } from '@core/AssetLoader';
import { Registry } from '@core/Registry';
import { EntityRegistry } from '@entities/EntityLoader';
import { DOMUtils } from '@core/DOMUtils';
import { environmentRenderer } from './EnvironmentRenderer';
import { drawStatusBars } from './HeroRendererStatusBars';
import { drawRangeCircles } from './HeroRendererRangeCircles';
import { drawShadow } from './HeroRendererShadow';
import { drawWeapon } from './HeroRendererWeapon';
import { Hero } from '../gameplay/Hero';
import type { IEntity, IGame, ISystem } from '../types/core';

class HeroRendererSystem implements ISystem {
    // Cached image properties
    private _heroPath: string | null = null;
    private _heroImg: HTMLImageElement | null = null;
    private _heroCanvas: HTMLCanvasElement | null = null;
    private _heroW: number | null = null;
    private _heroH: number | null = null;
    private _shadowImg: HTMLImageElement | HTMLCanvasElement | null = null;

    constructor() {
        Logger.info('[HeroRenderer] Initialized');
    }

    /** System initialization */
    init(_game: IGame): void {
        // No specific init logic needed yet
    }

    /**
     * Set the hero skin - clears cached images and loads new skin
     * @param {string} skinId - Hero skin ID (e.g., 'hero_t1_01')
     */
    setSkin(skinId: string) {
        // Get skin data from EntityRegistry
        const skinData = EntityRegistry?.hero?.[skinId];
        if (!skinData) {
            Logger.warn(`[HeroRenderer] Skin not found: ${skinId}`);
            return;
        }

        // Build image path from skin files
        let path = null;
        if (skinData.files?.clean) {
            path = 'assets/' + skinData.files.clean;
        } else if (skinData.files?.original) {
            path = 'assets/' + skinData.files.original;
        }

        if (!path) {
            Logger.warn(`[HeroRenderer] No image path for skin: ${skinId}`);
            return;
        }

        // Clear all cached images to force reload
        this._heroPath = path;
        this._heroImg = null;
        this._heroCanvas = null;
        this._heroW = null;
        this._heroH = null;
        this._shadowImg = null;

        Logger.info(`[HeroRenderer] Skin changed to: ${skinId} -> ${path}`);
    }

    /**
     * Render the hero and their equipped weapon
     * @param {CanvasRenderingContext2D} ctx
     * @param {Hero} hero
     */
    render(ctx: CanvasRenderingContext2D, hero: Hero, includeShadow = true, alpha = 1) {
        if (!hero || !hero.active) return;

        // Interpolation
        const prevX = (hero.prevX !== undefined) ? hero.prevX : hero.x;
        const prevY = (hero.prevY !== undefined) ? hero.prevY : hero.y;

        const renderX = prevX + (hero.x - prevX) * alpha;
        const renderY = prevY + (hero.y - prevY) * alpha;

        const originalX = hero.x;
        const originalY = hero.y;

        // Apply interpolated coordinates for rendering
        const heroPos = hero as { x: number; y: number };
        heroPos.x = renderX;
        heroPos.y = renderY;

        try {
            if (includeShadow) {
                this._ensureShadowImg();
                drawShadow(ctx, hero, {
                    shadowImg: this._shadowImg,
                    forceOpaque: false
                });
            }

            drawRangeCircles(ctx, hero);

            this.drawBody(ctx, hero);

            drawWeapon(ctx, hero);

            drawStatusBars(ctx, hero);
        } finally {
            // Restore actual physics coordinates
            const heroPos = hero as { x: number; y: number };
            heroPos.x = originalX;
            heroPos.y = originalY;
        }
    }

    private _ensureShadowImg(): void {
        if (!this._shadowImg && MaterialLibrary) {
            this._shadowImg = MaterialLibrary.get('world_hero', 'shadow', {});
        }
    }

    /** For ShadowRenderer compatibility */
    drawShadow(ctx: CanvasRenderingContext2D, hero: Hero, forceOpaque = false) {
        this._ensureShadowImg();
        drawShadow(ctx, hero, { shadowImg: this._shadowImg, forceOpaque });
    }

    /**
     * Draw the main hero sprite
     */
    drawBody(ctx: CanvasRenderingContext2D, hero: Hero) {
        // PERF: Cache heroPath on renderer - use saved skin or default
        if (!this._heroPath) {
            const savedSkin = localStorage.getItem('heroSelectedSkin') || 'hero_t1_01';
            const skinData = EntityRegistry?.hero?.[savedSkin];

            if (skinData?.files?.clean) {
                this._heroPath = 'assets/' + skinData.files.clean;
            } else if (skinData?.files?.original) {
                this._heroPath = 'assets/' + skinData.files.original;
            } else if (AssetLoader) {
                // Fallback to world_hero asset
                this._heroPath = AssetLoader.getImagePath('world_hero');
            }
        }

        if (this._heroPath) {
            // Lazy load image on the renderer instance
            if (!this._heroImg) {
                this._heroImg = AssetLoader.createImage(this._heroPath);
            }

            // Wait for image to be fully processed (white removal converts src to data URL)
            const isProcessed =
                this._heroImg.src.startsWith('data:') || this._heroImg.src.includes('PH.png');
            const isLoaded = this._heroImg.complete && this._heroImg.naturalWidth;

            if (isLoaded && isProcessed) {
                // PERF: Cache scaled sprite to avoid expensive resizing every frame
                // Invalidate cache if dimensions change
                if (
                    !this._heroCanvas ||
                    this._heroW !== hero.width ||
                    this._heroH !== hero.height
                ) {
                    this._heroW = hero.width;
                    this._heroH = hero.height;
                    this._heroCanvas = DOMUtils.createCanvas(hero.width, hero.height);
                    const c = this._heroCanvas.getContext('2d');
                    c.imageSmoothingEnabled = false;
                    c.drawImage(this._heroImg, 0, 0, hero.width, hero.height);
                }

                ctx.drawImage(this._heroCanvas, hero.x - hero.width / 2, hero.y - hero.height / 2);
            }
        }
        // No fallback - skip rendering until sprite loads
    }
}

// Create singleton and export
const HeroRenderer = new HeroRendererSystem();
if (Registry) Registry.register('HeroRenderer', HeroRenderer);

export { HeroRendererSystem, HeroRenderer };

/**
 * ResourceRenderer
 * Handles rendering for all resources.
 */

import { Logger } from '@core/Logger';
import { MaterialLibrary } from '@vfx/MaterialLibrary';
import { ProgressBarRenderer } from '@vfx/ProgressBarRenderer';
import { AssetLoader } from '@core/AssetLoader';
import { Registry } from '@core/Registry';
import { environmentRenderer } from './EnvironmentRenderer';
import { Resource } from '../gameplay/Resource';
import { DroppedItem } from '../gameplay/DroppedItem';
import { renderDroppedItem } from './ResourceRendererDropped';
import { ColorPalette } from '@config/ColorPalette';
import { EntityRegistry } from '@entities/EntityLoader';
import { GameConstants } from '@data/GameConstants';
import type { IGame, ISystem } from '../types/core';

class ResourceRendererService implements ISystem {
    constructor() {
        Logger.info('[ResourceRenderer] Initialized');
    }

    init(_game: IGame): void { }

    render(ctx: CanvasRenderingContext2D, res: Resource, includeShadow = true) {
        if (!res.active) return;

        // Shadow
        if (includeShadow) {
            this.renderShadow(ctx, res);
        }

        // Shake logic (if damaged) - skipped for simplification or handled by Tween?
        // Check if this resource type uses custom rendering (e.g., trees handled elsewhere)
        const typeConfig =
            EntityRegistry?.nodes?.[res.resourceType] || EntityRegistry?.resources?.[res.resourceType] || {};
        if (typeConfig.skipDefaultRender) return;

        if (res.state === 'depleted') {
            this.renderDepleted(ctx, res);
            return;
        }

        // Active State
        this.renderActive(ctx, res);
    }

    // ... rest of methods ...
    renderShadow(ctx: CanvasRenderingContext2D, res: Resource, forceOpaque = false) {
        // Check environmentRenderer singleton for dynamic shadows
        const env = environmentRenderer;

        let scaleY = 0.3;
        let alpha = 0.3;

        if (env) {
            scaleY = env.shadowScaleY;
            alpha = env.shadowAlpha;
        }

        // 1. Static Contact Shadow
        ctx.save();
        ctx.translate(res.x, res.y + res.height / 2 - 6);

        if (forceOpaque) {
            ctx.fillStyle = 'black';
            ctx.globalAlpha = 1.0;
        } else {
            ctx.fillStyle = 'black';
            ctx.globalAlpha = alpha;
        }

        const contactWidth = res.width * 0.6;
        const contactHeight = res.height * 0.15;

        ctx.beginPath();
        ctx.ellipse(0, 0, contactWidth / 2, contactHeight / 2, 0, 0, Math.PI * 2);
        ctx.fill();
        ctx.restore();

        // 2. Dynamic Shadow
        ctx.save();
        ctx.translate(res.x, res.y + res.height / 2 - 6);

        const skew = env ? env.shadowSkew || 0 : 0;
        ctx.transform(1, 0, skew, 1, 0, 0);

        ctx.scale(1, -scaleY); // Flip resource shadows vertical

        if (forceOpaque) {
            ctx.globalAlpha = 1.0;
            ctx.fillStyle = 'black';
        } else {
            ctx.globalAlpha = alpha;
            ctx.fillStyle = 'black';
        }

        // PERF: Cache shadow image on entity (retry until successful)
        if (!res._shadowImg) {
            // Use same asset ID logic as renderActive
            let assetId = null;
            if (res.resourceType && res.resourceType.startsWith('node_')) {
                assetId = res.resourceType;
            } else {
                const entityData =
                    EntityRegistry?.nodes?.[res.resourceType] ||
                    EntityRegistry?.resources?.[res.resourceType];
                if (entityData?.sprite) {
                    assetId = entityData.sprite;
                } else if (entityData?.id) {
                    assetId = entityData.id;
                } else {
                    assetId = res.resourceType;
                }
            }
            if (MaterialLibrary && assetId) {
                res._shadowImg = MaterialLibrary.get(assetId, 'shadow', {});
            }
        }

        if (res._shadowImg) {
            // Draw anchored at bottom center (-w/2, -h)
            ctx.drawImage(res._shadowImg, -res.width / 2, -res.height, res.width, res.height);
        } else {
            // Fallback: Geometric Shadow
            ctx.beginPath();
            ctx.ellipse(0, -res.height / 4, res.width / 2, res.height / 2, 0, 0, Math.PI * 2);
            ctx.fill();
        }

        ctx.restore();
    }

    renderDepleted(ctx: CanvasRenderingContext2D, res: Resource) {
        // For node_* types, use resourceType directly as base asset ID
        let baseAssetId = null;
        if (res.resourceType && res.resourceType.startsWith('node_')) {
            baseAssetId = res.resourceType;
        } else {
            // Fallback: try EntityRegistry lookup for legacy types
            const entityData =
                EntityRegistry?.nodes?.[res.resourceType] ||
                EntityRegistry?.resources?.[res.resourceType];
            if (entityData?.sprite) {
                baseAssetId = entityData.sprite;
            } else if (entityData?.id) {
                baseAssetId = entityData.id;
            } else {
                baseAssetId = 'world_' + res.resourceType;
            }
        }

        const consumedAssetId = baseAssetId + '_consumed';
        const consumedPath = AssetLoader ? AssetLoader.getImagePath(consumedAssetId) : null;

        // Simple image load logic, in real engine use AssetLoader.get()
        if (consumedPath) {
            // We can't easily cache image on the renderer without a map.
            // Rely on browser caching or check res._consumedImage (legacy data prop)
            if (!res._consumedImage) {
                res._consumedImage = AssetLoader.createImage(consumedPath);
            }
            if (res._consumedImage.complete && res._consumedImage.naturalWidth) {
                ctx.drawImage(
                    res._consumedImage,
                    res.x - res.width / 2,
                    res.y - res.height / 2,
                    res.width,
                    res.height
                );
            }
        } else {
            // Fallback
            ctx.save();
            ctx.globalAlpha = 0.4;
            ctx.fillStyle = '#222222';
            ctx.beginPath();
            ctx.arc(res.x, res.y, res.width / 2, 0, Math.PI * 2);
            ctx.fill();
            ctx.arc(res.x, res.y, res.width / 2, 0, Math.PI * 2);
            ctx.fill();
            ctx.restore();
        }

        // Use ProgressBarRenderer for Respawn Timer
        if (ProgressBarRenderer && res.respawnTimer > 0 && res.maxRespawnTime > 0) {
            // Calculate percentage (0 to 1 filling up)
            const remaining = res.respawnTimer;
            const total = res.maxRespawnTime;
            const pct = 1 - remaining / total;

            const R = GameConstants.Resource;
            ProgressBarRenderer.draw(ctx, {
                x: res.x - R.HEALTH_BAR_OFFSET_X,
                y: res.y - res.height / 2 - R.HEALTH_BAR_Y_OFFSET,
                width: R.HEALTH_BAR_WIDTH,
                height: R.HEALTH_BAR_HEIGHT,
                percent: pct,
                mode: 'respawn',
                animated: true
            });
        }
    }

    renderActive(ctx: CanvasRenderingContext2D, res: Resource) {
        // For node_* types, use resourceType directly as asset key (matches AssetLoader)
        let assetId = null;
        if (res.resourceType && res.resourceType.startsWith('node_')) {
            assetId = res.resourceType;
        } else {
            // Fallback: try EntityRegistry lookup for legacy types
            const entityData =
                EntityRegistry?.nodes?.[res.resourceType] ||
                EntityRegistry?.resources?.[res.resourceType];
            if (entityData?.sprite) {
                assetId = entityData.sprite;
            } else if (entityData?.id) {
                assetId = entityData.id;
            } else {
                // Last resort: legacy world_ prefix
                assetId = 'world_' + res.resourceType;
            }
        }

        const imagePath = AssetLoader ? AssetLoader.getImagePath(assetId) : null;

        if (imagePath) {
            if (!res._spriteImage) {
                res._spriteImage = AssetLoader.createImage(imagePath);
            }
            if (res._spriteImage.complete && res._spriteImage.naturalWidth) {
                ctx.drawImage(
                    res._spriteImage,
                    res.x - res.width / 2,
                    res.y - res.height / 2,
                    res.width,
                    res.height
                );
                return;
            }
        }

        // Fallback Procedural
        ctx.save();
        ctx.shadowColor = res.color;
        ctx.shadowBlur = 8;
        ctx.fillStyle = res.color;
        ctx.beginPath();
        ctx.roundRect(res.x - res.width / 2, res.y - res.height / 2, res.width, res.height, 6);
        ctx.fill();
        ctx.shadowBlur = 0;
        ctx.strokeStyle = '#1A1A2E';
        ctx.lineWidth = 2;
        ctx.stroke();

        // Letter
        ctx.fillStyle = '#FFFFFF';
        ctx.font = 'bold 16px sans-serif';
        ctx.textAlign = 'center';
        ctx.textBaseline = 'middle';
        const letter = res.resourceType.charAt(0).toUpperCase();
        ctx.fillText(letter, res.x, res.y);
        ctx.restore();

        // --- Progress Bar (Health) ---
        // Check for health (Component or Direct)
        let currentHealth = 0;
        let maxHealth = 0;

        if (res.components && res.components.health) {
            currentHealth = res.components.health.health;
            maxHealth = res.components.health.maxHealth;
        } else if (res.health !== undefined) {
            currentHealth = res.health;
            maxHealth = res.maxHealth || GameConstants.Combat.DEFAULT_MAX_HEALTH_NPC;
        }

        // Only draw if damaged and not dead
        if (currentHealth < maxHealth && currentHealth > 0) {
            if (ProgressBarRenderer) {
                const pct = currentHealth / maxHealth;
                const R = GameConstants.Resource;
                ProgressBarRenderer.draw(ctx, {
                    x: res.x - R.HEALTH_BAR_OFFSET_X,
                    y: res.y - res.height / 2 - R.HEALTH_BAR_Y_OFFSET,
                    width: R.HEALTH_BAR_WIDTH,
                    height: R.HEALTH_BAR_HEIGHT,
                    percent: pct,
                    mode: 'health',
                    entityId: res.id // For damage trail
                });
            }
        }
    }

    renderDroppedItem(ctx: CanvasRenderingContext2D, item: DroppedItem) {
        renderDroppedItem(ctx, item);
    }
}

