/**
 * WorldRendererMapgen4 - Renders mapgen4 polygon mesh to canvas
 *
 * Uses WorldManager mesh+map. Viewport in world coords, converted to mesh coords (0..1000).
 * Railroad is rendered as its own pass AFTER the terrain/rivers/roads (not inside the
 * preview function) so the game pipeline has full control and visibility.
 *
 * Owner: Director
 */

import { Logger } from '@core/Logger';
import { Registry } from '@core/Registry';
import * as PIXI from 'pixi.js';
import { drawCachedMeshToCanvas } from '../tools/map-editor/Mapgen4PreviewRenderer';
import { createRailroadMeshes } from '../tools/map-editor/RailroadMeshRenderer';
import { MapEditorConfig } from '../tools/map-editor/MapEditorConfig';
import type { Mesh } from '../tools/map-editor/mapgen4/types';
import type Mapgen4Map from '../tools/map-editor/mapgen4/map';
import type { Mapgen4Param, RailroadCrossing, TownSite, RoadSegment } from '../tools/map-editor/Mapgen4Param';
import type { IGame, IViewport } from '../types/core';

const MESH_SIZE = 1000;
const WORLD_SIZE = 160000;

type WorldManagerLike = {
    getMesh: () => { mesh: Mesh; map: Mapgen4Map } | null;
    getMapgen4Param: () => Mapgen4Param;
    getCachedTownsAndRoads?: () => {
        towns: TownSite[];
        roadSegments: RoadSegment[];
        railroadPath: number[];
        railroadCrossings: RailroadCrossing[];
        railroadStationIds: number[];
    };
};

class WorldRendererMapgen4 {
    game: IGame | null = null;
    _worldManager: WorldManagerLike | null = null;
    private _loggedOnce = false;
    private _pixiApp: PIXI.Application | null = null;
    private _pixiAppInitPromise: Promise<void> | null = null;
    private _railroadContainer: PIXI.Container | null = null;
    private _lastRailroadPath: string = '';
    private _railroadMeshes: PIXI.Mesh[] = [];

    constructor() {
        Logger.info('[WorldRendererMapgen4] Constructed');
    }

    init(game: IGame) {
        this.game = game;
        this._worldManager = game.getSystem('WorldManager') as typeof this._worldManager;
        if (!this._worldManager) {
            this._worldManager = Registry?.get('WorldManager') as typeof this._worldManager;
        }
        Logger.info('[WorldRendererMapgen4] Initialized');
    }

    render(ctx: CanvasRenderingContext2D, viewport: IViewport) {
        if (!this.game || !this._worldManager) return;

        const meshAndMap = this._worldManager.getMesh();
        if (!meshAndMap) return;

        const param = this._worldManager.getMapgen4Param();
        const { mesh, map } = meshAndMap;

        // Viewport in world coords  mesh coords (0..1000)
        const vpX = (viewport.x / WORLD_SIZE) * MESH_SIZE;
        const vpY = (viewport.y / WORLD_SIZE) * MESH_SIZE;
        const vpW = (viewport.width / WORLD_SIZE) * MESH_SIZE;
        const vpH = (viewport.height / WORLD_SIZE) * MESH_SIZE;

        if (!ctx?.canvas) return;

        const { towns, roadSegments, railroadPath, railroadCrossings, railroadStationIds = [] } =
            this._worldManager.getCachedTownsAndRoads?.() ?? {
                towns: [],
                roadSegments: [],
                railroadPath: [],
                railroadCrossings: [],
                railroadStationIds: []
            };

        // One-time diagnostic log to confirm railroad data is present
        if (!this._loggedOnce) {
            Logger.info(
                '[WorldRendererMapgen4] First render  railroadPath:',
                railroadPath.length,
                'towns:', towns.length,
                'roads:', roadSegments.length,
                'canvas:', ctx.canvas.width, 'x', ctx.canvas.height,
                'meshVP:', vpX.toFixed(1), vpY.toFixed(1), vpW.toFixed(3), vpH.toFixed(3)
            );
            this._loggedOnce = true;
        }

        // 1. Terrain, rivers, roads  skip railroad (drawn as separate pass below)
        drawCachedMeshToCanvas(
            ctx,
            mesh,
            map,
            param,
            vpX,
            vpY,
            vpW,
            vpH,
            towns,
            roadSegments,
            railroadPath,
            railroadCrossings,
            railroadStationIds,
            new Set<string>(), // no hidden zones in game
            true // skipRailroad  drawn explicitly below
        );

        // 2. Railroad  separate pass, directly on game canvas
        if (railroadPath.length >= 2) {
            this.renderRailroad(
                ctx, mesh, map, param, railroadPath, railroadCrossings, railroadStationIds,
                vpX, vpY, vpW, vpH
            );
        }
    }

    /**
     * Draw railroad directly to the game canvas using the same meshcanvas mapping
     * as drawCachedMeshToCanvas. This gives the game full control over railroad rendering
     * instead of relying on the map editor preview function's internal drawing.
     */
    private renderRailroad(
        ctx: CanvasRenderingContext2D,
        mesh: Mesh,
        map: Mapgen4Map,
        param: Mapgen4Param,
        railroadPath: number[],
        railroadCrossings: RailroadCrossing[],
        railroadStationIds: number[],
        vpX: number,
        vpY: number,
        vpW: number,
        vpH: number
    ): void {
        const w = ctx.canvas.width;
        const h = ctx.canvas.height;
        if (w === 0 || h === 0) return;

        const scaleX = w / vpW;
        const scaleY = h / vpH;
        const scale = Math.min(scaleX, scaleY);

        const toCanvas = (x: number, y: number) => ({
            x: (x - vpX) * scaleX,
            y: (y - vpY) * scaleY
        });

        // Initialize PIXI App asynchronously if needed for WebGL context to render true continuous UV meshes
        if (!this._pixiApp && !this._pixiAppInitPromise) {
            this._pixiApp = new PIXI.Application();

            // PIXI v8 requires async initialization. Catch errors to prevent silent fails.
            this._pixiAppInitPromise = this._pixiApp.init({
                width: w,
                height: h,
                backgroundAlpha: 0,
                autoStart: false,
                preserveDrawingBuffer: true
            }).then(() => {
                this._railroadContainer = new PIXI.Container();
                this._pixiApp!.stage.addChild(this._railroadContainer);
            }).catch(err => {
                Logger.error('[WorldRendererMapgen4] PIXI Railroad Renderer init failed:', err);
            });
        }

        // The game render loop is synchronous. Fall back to simple lines while PIXI mounts in the background
        if (!this._pixiApp || !this._railroadContainer || !this._pixiApp.canvas) {
            this.renderRailroadFallbackLines(ctx, mesh, railroadPath, toCanvas);
            return;
        }

        // Ensure canvas stays synchronized with game viewport resizes
        if (this._pixiApp.canvas.width !== w || this._pixiApp.canvas.height !== h) {
            this._pixiApp.renderer.resize(w, h);
        }

        // Rebuild mesh geometry if the path changes
        const currentPathStr = railroadPath.join(',');
        if (this._lastRailroadPath !== currentPathStr) {
            this._lastRailroadPath = currentPathStr;

            // Clean up old meshes to prevent WebGL buffer leaks
            for (const m of this._railroadMeshes) {
                m.destroy({ children: true, texture: false });
            }
            this._railroadContainer!.removeChildren();

            this._railroadMeshes = createRailroadMeshes(
                mesh,
                map,
                railroadPath,
                this._railroadContainer!,
                railroadStationIds
            );
        }

        if (this._railroadMeshes.length > 0) {
            // Transform the container to match the viewport camera.
            // Mapgen4 coordinates (rx, ry) in mesh space (0-1000).
            this._railroadContainer!.scale.set(scaleX, scaleY);
            this._railroadContainer!.position.set(-vpX * scaleX, -vpY * scaleY);

            // Render the PIXI stage to its internal WebGL canvas
            this._pixiApp.renderer.render(this._pixiApp.stage);

            // Composite the PIXI WebGL canvas onto the Game's native 2D canvas
            // PIXI v8 uses .canvas instead of .view
            ctx.drawImage(this._pixiApp.canvas, 0, 0);
        } else {
            // Fallback: if the spline fails to produce visible output
            this.renderRailroadFallbackLines(ctx, mesh, railroadPath, toCanvas);
        }
    }

    /**
     * Simple thick lines between railroad station points.
     * Always drawn ON TOP of the spline as a guarantee of visibility.
     * Uses a semi-transparent dark line so it doesn't overpower textures
     * but is clearly visible if the spline rendering fails.
     */
    private renderRailroadFallbackLines(
        ctx: CanvasRenderingContext2D,
        mesh: Mesh,
        railroadPath: number[],
        toCanvas: (x: number, y: number) => { x: number; y: number }
    ): void {
        if (railroadPath.length < 2) return;

        ctx.save();
        ctx.strokeStyle = 'rgba(90, 60, 30, 0.7)';
        ctx.lineWidth = 4;
        ctx.lineCap = 'round';
        ctx.lineJoin = 'round';

        ctx.beginPath();
        const first = toCanvas(mesh.x_of_r(railroadPath[0]), mesh.y_of_r(railroadPath[0]));
        ctx.moveTo(first.x, first.y);

        for (let i = 1; i < railroadPath.length; i++) {
            const p = toCanvas(
                mesh.x_of_r(railroadPath[i]),
                mesh.y_of_r(railroadPath[i])
            );
            ctx.lineTo(p.x, p.y);
        }
        ctx.stroke();
        ctx.restore();
    }
}

const worldRendererMapgen4 = new WorldRendererMapgen4();
if (Registry) Registry.register('WorldRendererMapgen4', worldRendererMapgen4);

export { WorldRendererMapgen4, worldRendererMapgen4 };

/**
 * BossSystem
 * Manages boss spawning, respawn timers, and encounter tracking.
 *
 * Features:
 * - One boss per biome
 * - Respawn on timer after death
 * - Spawn on biome entry or game start
 *
 * Work Package: 09-boss-system.md
 */

import { Logger } from '@core/Logger';
import { EventBus } from '@core/EventBus';
import { GameConstants, getConfig } from '@data/GameConstants';
import { entityManager } from '@core/EntityManager';
import { Registry } from '@core/Registry';
import { EntityRegistry } from '@entities/EntityLoader';
import { BiomeConfig } from '@data/BiomeConfig';
import { EntityTypes } from '@config/EntityTypes';
import { Boss } from '../gameplay/Boss';
import type { IGame, IEntity } from '../types/core';

class BossSystem {
    game: IGame | null = null;
    bosses: Map<string, Boss> = new Map();
    respawnTimers: Map<string, number> = new Map();

    constructor() {
        Logger.info('[BossSystem] Constructed');
    }

    init(game: IGame) {
        this.game = game;
        this.initListeners();

        const delay = GameConstants.Timing.BOSS_SPAWN_DELAY_MS;
        setTimeout(() => {
            this.spawnAllBosses();
        }, delay);

        Logger.info('[BossSystem] Initialized');
    }

    initListeners() {
        if (EventBus && GameConstants?.Events) {
            // Listen for enemy death to track boss deaths
            EventBus.on('ENEMY_DIED', (data: { entity: IEntity }) => this.onEnemyDied(data));

            // Listen for biome entry to spawn bosses
            EventBus.on(GameConstants.Events.BIOME_ENTERED, (data: { biomeId: string }) =>
                this.onBiomeEntered(data)
            );
        }
    }

    /**
     * Spawn boss for a biome
     * @param {string} biomeId
     */
    spawnBoss(biomeId: string) {
        // Check if already spawned or respawning
        if (this.bosses.has(biomeId) || this.respawnTimers.has(biomeId)) {
            return null;
        }

        // Get biome config
        const biome = (BiomeConfig?.types as Record<string, { bossId?: string; bossSpawn?: { x: number; y: number }; bounds?: { x: number; y: number; width: number; height: number }; [key: string]: unknown }>)?.[biomeId];
        if (!biome?.bossId) {
            Logger.info(`[BossSystem] No boss configured for biome: ${biomeId}`);
            return null;
        }

        // Get boss type config
        const bossConfig = EntityRegistry.bosses?.[biome.bossId];
        if (!bossConfig) {
            Logger.info(`[BossSystem] Boss type not found: ${biome.bossId}`);
            return null;
        }

        // Get spawn position
        const spawnPos = this.getBossSpawnPosition(biomeId);

        // Create boss
        const boss = new Boss({
            x: spawnPos.x,
            y: spawnPos.y,
            bossType: biome.bossId,
            biomeId: biomeId,
            level: biome.levelRange?.max ?? GameConstants.Boss.DEFAULT_LEVEL
        });

        // Register with EntityManager
        if (entityManager) {
            entityManager.add(boss);
        }

        // Track
        this.bosses.set(biomeId, boss);

        // Emit spawn event
        if (EventBus && GameConstants?.Events) {
            EventBus.emit(GameConstants.Events.BOSS_SPAWNED, {
                boss,
                biomeId,
                bossType: biome.bossId
            });
        }

        Logger.info(`[BossSystem] Spawned ${boss.bossName} in ${biomeId}`);
        return boss;
    }

    /**
     * Get spawn position for boss
     * @param {string} biomeId
     * @returns {{x: number, y: number}}
     */
    getBossSpawnPosition(biomeId: string) {
        // Try to get from biome config
        const biome = (BiomeConfig?.types as Record<string, { bossSpawn?: { x: number; y: number }; bounds?: { x: number; y: number; width: number; height: number }; [key: string]: unknown }>)?.[biomeId];
        if (biome?.bossSpawn) {
            return biome.bossSpawn;
        }

        // Try to calculate from biome bounds
        if (biome?.bounds) {
            return {
                x: biome.bounds.x + biome.bounds.width / 2,
                y: biome.bounds.y + biome.bounds.height / 2
            };
        }

        const offsetX = GameConstants.World.IRONHAVEN_OFFSET_X;
        const offsetY = GameConstants.World.IRONHAVEN_OFFSET_Y;
        const offsets = GameConstants?.Boss?.SPAWN_OFFSETS;
        const g = offsets?.grasslands ?? { x: 5500, y: 3000 };
        const t = offsets?.tundra ?? { x: 3000, y: 5500 };
        const d = offsets?.desert ?? { x: 5500, y: 5500 };
        const b = offsets?.badlands ?? { x: 3000, y: 3000 };
        const def = offsets?.default ?? { x: 3500, y: 3500 };
        const defaults: Record<string, { x: number; y: number }> = {
            grasslands: { x: offsetX + g.x, y: offsetY + g.y },
            tundra: { x: offsetX + t.x, y: offsetY + t.y },
            desert: { x: offsetX + d.x, y: offsetY + d.y },
            badlands: { x: offsetX + b.x, y: offsetY + b.y }
        };
        return defaults[biomeId] ?? { x: offsetX + def.x, y: offsetY + def.y };
    }

    /**
     * Handle enemy death - track boss deaths
     */
    onEnemyDied(data: { entity: IEntity; enemy?: IEntity & { isBoss?: boolean; biomeId?: string; respawnTime?: number; bossName?: string } }) {
        const enemy = data.enemy || data.entity;
        if (!enemy?.isBoss) return;

        const biomeId = enemy.biomeId;
        if (!biomeId) return;

        // Remove from active bosses
        this.bosses.delete(biomeId);

        const msPerSecond = GameConstants.Timing.MS_PER_SECOND;
        const respawnTime = enemy.respawnTime * msPerSecond;
        this.respawnTimers.set(biomeId, respawnTime);

        Logger.info(`[BossSystem] ${enemy.bossName} killed. Respawning in ${enemy.respawnTime}s`);
    }

    /**
     * Handle biome entry - spawn boss if not already spawned
     */
    onBiomeEntered(data: { biomeId: string }) {
        const { biomeId } = data;
        if (!biomeId) return;

        // Spawn boss if not already active or respawning
        if (!this.bosses.has(biomeId) && !this.respawnTimers.has(biomeId)) {
            this.spawnBoss(biomeId);
        }
    }

    /**
     * Update respawn timers
     * @param {number} dt - Delta time in milliseconds
     */
    update(dt: number) {
        // Update respawn timers
        for (const [biomeId, timer] of this.respawnTimers.entries()) {
            const newTimer = timer - dt;

            if (newTimer <= 0) {
                this.respawnTimers.delete(biomeId);
                this.spawnBoss(biomeId);
            } else {
                this.respawnTimers.set(biomeId, newTimer);
            }
        }
    }

    /**
     * Check if boss is alive in biome
     * @param {string} biomeId
     * @returns {boolean}
     */
    isBossAlive(biomeId: string) {
        const boss = this.bosses.get(biomeId);
        return boss && !boss.isDead && boss.active;
    }

    /**
     * Get active boss for biome
     * @param {string} biomeId
     * @returns {Boss|null}
     */
    getBoss(biomeId: string) {
        return this.bosses.get(biomeId) || null;
    }

    /**
     * Get time until boss respawn (in seconds)
     * @param {string} biomeId
     * @returns {number}
     */
    getRespawnTime(biomeId: string) {
        const timer = this.respawnTimers.get(biomeId);
        const ms = GameConstants.Timing.MS_PER_SECOND;
        return timer ? Math.ceil(timer / ms) : 0;
    }

    /**
     * Spawn all bosses for testing/initial load
     */
    spawnAllBosses() {
        const biomes = BiomeConfig?.types;
        if (!biomes) return;

        for (const biomeId of Object.keys(biomes)) {
            if ((biomes as Record<string, { bossId?: string; [key: string]: unknown }>)[biomeId]?.bossId) {
                this.spawnBoss(biomeId);
            }
        }
    }
}

