/**
 * CollisionSystemCollision  Collision detection and trigger event handling.
 */
import { Entity } from '../core/Entity';
import { WorldManager } from '../world/WorldManager';
import { GameConstants } from '../data/GameConstants';
import { EventBus } from '../core/EventBus';
import { getCollisionBounds, isHardCollision, isTriggerCollision } from './CollisionSystemUtils';

export function checkCollision(
    entity: Entity,
    spatialHash: Map<string, Entity[]>,
    cellSize: number,
    getBounds: (e: Entity) => { x: number; y: number; width: number; height: number }
): boolean {
    const col = entity.collision;
    const bounds = getBounds(entity);

    if (WorldManager.isBlocked(bounds.x, bounds.y) ||
        WorldManager.isBlocked(bounds.x + bounds.width, bounds.y) ||
        WorldManager.isBlocked(bounds.x, bounds.y + bounds.height) ||
        WorldManager.isBlocked(bounds.x + bounds.width, bounds.y + bounds.height)) {
        return true;
    }

    const startX = Math.floor(bounds.x / cellSize);
    const startY = Math.floor(bounds.y / cellSize);
    const endX = Math.floor((bounds.x + bounds.width) / cellSize);
    const endY = Math.floor((bounds.y + bounds.height) / cellSize);

    for (let x = startX; x <= endX; x++) {
        for (let y = startY; y <= endY; y++) {
            const key = `${x},${y}`;
            const neighbors = spatialHash.get(key);
            if (!neighbors) continue;

            for (const other of neighbors) {
                if (other === entity || !other.active) continue;

                const otherCol = other.collision;
                if (!otherCol || !otherCol.enabled) continue;

                if (col && isHardCollision(col, otherCol)) {
                    if (entity.collidesWith(other)) return true;
                }
            }
        }
    }
    return false;
}

export function checkTriggers(
    entity: Entity,
    spatialHash: Map<string, Entity[]>,
    cellSize: number,
    activeCollisions: Map<string, Set<string>>,
    currentOverlapsScratch: Set<string>,
    entities: Entity[],
    getBounds: (e: Entity) => { x: number; y: number; width: number; height: number }
): void {
    const col = entity.collision;
    if (!col) return;

    const bounds = getBounds(entity);
    const startX = Math.floor(bounds.x / cellSize);
    const startY = Math.floor(bounds.y / cellSize);
    const endX = Math.floor((bounds.x + bounds.width) / cellSize);
    const endY = Math.floor((bounds.y + bounds.height) / cellSize);

    currentOverlapsScratch.clear();

    for (let x = startX; x <= endX; x++) {
        for (let y = startY; y <= endY; y++) {
            const key = `${x},${y}`;
            const neighbors = spatialHash.get(key);
            if (!neighbors) continue;

            for (const other of neighbors) {
                if (other === entity || !other.active) continue;

                const otherCol = other.collision;
                if (!otherCol || !otherCol.enabled) continue;

                if (isTriggerCollision(col, otherCol) && entity.collidesWith(other)) {
                    currentOverlapsScratch.add(other.id);
                }
            }
        }
    }

    let previousOverlaps = activeCollisions.get(entity.id);
    if (!previousOverlaps) {
        previousOverlaps = new Set();
        activeCollisions.set(entity.id, previousOverlaps);
    }

    for (const otherId of currentOverlapsScratch) {
        if (!previousOverlaps.has(otherId)) {
            const other = entities.find((e) => e.id === otherId);
            if (other) EventBus.emit(GameConstants.Events.COLLISION_START, { a: entity, b: other });
        }
    }

    for (const otherId of previousOverlaps) {
        if (!currentOverlapsScratch.has(otherId)) {
            const other = entities.find((e) => e.id === otherId);
            if (other) EventBus.emit(GameConstants.Events.COLLISION_END, { a: entity, b: other });
        }
    }

    previousOverlaps.clear();
    for (const id of currentOverlapsScratch) previousOverlaps.add(id);
}

/**
 * CombatController - Manages combat targeting, attacks, and loot drops
 *
 * Extracted from Game.js to separate combat logic from the core game loop.
 * Handles auto-targeting, attack execution, and item spawning.
 *
 * Owner: Gameplay Engineer
 */

import { Logger } from '@core/Logger';
import { GameConstants } from '@data/GameConstants';
import { entityManager } from '@core/EntityManager';
import { Registry } from '@core/Registry';
import { heroSystem } from './HeroSystem';
import { MathUtils } from '@core/MathUtils';
import type { IGame, IEntity } from '../types/core';

// Reference entityManager as EntityManager for global usage pattern
const EntityManager = entityManager;

class CombatController {
    game: IGame | null = null;
    _logTimer: number = 0;

    constructor() {
        Logger.info('[CombatController] Constructed');
    }

    /**
     * Initialize with game reference
     * @param {Game} game
     */
    init(game: IGame) {
        this.game = game;
        Logger.info('[CombatController] Initialized');
    }

    /**
     * Update combat logic
     * @param {number} dt - Delta time
     */
    update(_dt: number) {
        if (!this.game || !this.game.hero) return;

        // Debug Throttle (every ~60 frames)
        this._logTimer++;
        const doLog = this._logTimer % 60 === 0;

        const hero = this.game.hero;
        // Use EntityManager for efficient lookups
        const resources = EntityManager ? EntityManager.getByType('Resource') : [];
        const dinosaurs = EntityManager ? EntityManager.getByType('Dinosaur') : [];

        // Debug once per second
        if (doLog) {
            // Logger.info(`[CombatController] Entities - Resources: ${resources.length}, Dinos: ${dinosaurs.length}`);
        }

        const combat = hero.components && hero.components.combat;

        // Reset dinosaur attack flags
        for (const dino of dinosaurs) {
            if (dino.active) {
                dino.isBeingAttacked = false;
            }
        }

        // Find closest target (Resource or Dinosaur)
        let closestTarget = null;
        let closestDist = Infinity;
        let targetType = null;

        // Check resources (Mining Range)
        const miningDist =
            hero.miningRange || (combat ? combat.range : GameConstants.Combat.DEFAULT_MINING_RANGE);
        let i = 0;
        for (const resource of resources) {
            // Must be active AND ready (not depleted)
            if (resource.active && resource.state === 'ready') {
                const dist = resource.distanceTo(hero);

                if (dist <= miningDist && dist < closestDist) {
                    closestDist = dist;
                    closestTarget = resource;
                    targetType = 'resource';
                }
            }
            i++;
        }

        // Check dinosaurs (Gun Range) - use stats component for equipped weapon range
        const gunDist =
            hero.stats?.getAttackRange?.() ||
            (combat ? combat.range : GameConstants.Combat.DEFAULT_GUN_RANGE);

        for (const dino of dinosaurs) {
            if (dino.active) {
                const dist = dino.distanceTo(hero);
                // Dinos are valid targets if within gun range (and not dead)
                if (dino.state !== 'dead' && dist <= gunDist && dist < closestDist) {
                    closestDist = dist;
                    closestTarget = dino;
                    targetType = 'dinosaur';
                }
            }
        }

        // Check enemies (highest priority - hostile entities)
        // Note: Use constructor.name ('Enemy', 'Boss') since entityType varies
        const enemies = EntityManager
            ? [...EntityManager.getByType('Enemy'), ...EntityManager.getByType('Boss')]
            : [];

        for (const enemy of enemies) {
            if (enemy.active && !enemy.isDead) {
                const dist = enemy.distanceTo
                    ? enemy.distanceTo(hero)
                    : MathUtils.distance(enemy.x, enemy.y, hero.x, hero.y);
                // Enemies are valid targets within gun range
                if (dist <= gunDist && dist < closestDist) {
                    closestDist = dist;
                    closestTarget = enemy;
                    targetType = 'enemy';
                }
            }
        }

        // Auto-attack closest target
        if (closestTarget) {
            // Logger.info(`[Combat] Target found: ${targetType}`);
            // Freeze dinosaurs while being attacked
            if (targetType === 'dinosaur') {
                closestTarget.isBeingAttacked = true;
            }

            // Execute attack (Delegated to HeroSystem)
            let destroyed = false;
            if (heroSystem) {
                destroyed = heroSystem.tryAttack(hero, closestTarget);
            }

            if (destroyed) {
                this.handleTargetDestruction(closestTarget, targetType);
            }
        } else {
            hero.targetResource = null;
        }
    }

    /**
     * Handle logic when a target is destroyed (state updates, VFX)
     * NOTE: Drops are already created by Resource.takeDamage / DinosaurSystem
     * @param {Entity} target
     * @param {string} type - 'resource' or 'dinosaur'
     */
    handleTargetDestruction(_target: IEntity, _type: string) {
        // Drops are handled by the entity's own takeDamage method
        // This method is now only used for any additional destruction effects
        // (Currently none - preserved for future extension like VFX, audio, etc.)
    }
}

// Create singleton and export
const combatController = new CombatController();

// Register at module load time
Registry.register('CombatController', combatController);

export { CombatController, combatController };

/**
 * DamageSystem
 * Centralized handler for all damage, death, and combat VFX logic.
 *
 * Responsibilities:
 * - Listens for ENTITY_DAMAGED
 * - Applies damage to Health/Stats components
 * - Triggers Blood/Hit VFX
 * - Triggers Floating Text
 * - Handles Death (ENTITY_DIED) and Loot generation
 *
 * Owner: Combat System
 */

import { Logger } from '@core/Logger';
import { EventBus } from '@core/EventBus';
import { GameConstants } from '@data/GameConstants';
import { VFXController } from '@vfx/VFXController';
import { VFXConfig } from '@data/VFXConfig';
import { AudioManager } from '@audio/AudioManager';
import { Registry } from '@core/Registry';
import { EntityTypes } from '@config/EntityTypes';
import type { IGame, IEntity } from '../types/core.d';
import { isDamageable, isMortal, DamageableEntity } from '../utils/typeGuards';

const Events = GameConstants.Events;

class DamageSystem {
    game: IGame | null = null;

    private _onDamageBound: (data: { entity: IEntity; amount: number; source?: IEntity; type?: string }) => void;
    private _onDeathBound: (data: { entity: IEntity }) => void;

    constructor() {
        Logger.info('[DamageSystem] Initialized');
        // Bind handlers once
        this._onDamageBound = this.handleDamage.bind(this);
        this._onDeathBound = this.handleDeath.bind(this);

        this.initListeners();
    }

    init(game: IGame) {
        this.game = game;
    }

    initListeners() {
        if (EventBus) {
            EventBus.on(Events.ENTITY_DAMAGED, this._onDamageBound);
            EventBus.on(Events.ENTITY_DIED, this._onDeathBound);
        }
    }

    destroy() {
        if (EventBus) {
            EventBus.off(Events.ENTITY_DAMAGED, this._onDamageBound);
            EventBus.off(Events.ENTITY_DIED, this._onDeathBound);
            Logger.info('[DamageSystem] Cleared listeners');
        }
    }

    /**
     * Process incoming damage
     */
    handleDamage(data: { entity: IEntity; amount: number; source?: IEntity; type?: string }) {
        const { entity, amount, source, type } = data;
        if (!entity || !entity.active || entity.isDead) return;

        // 1. Apply Damage (using component if available)
        // Check for StatsComponent (Hero/Complex Entities)
        let finalDamage = amount;
        const healthComponent = entity.components?.health || entity.components?.stats;

        // Apply Armor reduction (simple flat reduction for now)
        if (entity.defense) {
            finalDamage = Math.max(1, finalDamage - entity.defense);
        }

        let tookDamage = false;

        if (healthComponent && typeof healthComponent.health === 'number') {
            // Data-only: apply damage and clamp; systems emit events
            Logger.info(
                `[DamageSystem] Applying ${finalDamage} damage to component on ${entity.id}`
            );
            healthComponent.health = Math.max(0, healthComponent.health - finalDamage);
            if (healthComponent.health <= 0) {
                healthComponent.isDead = true;
                healthComponent.health = 0;
            }
            tookDamage = true;
            const maxH = healthComponent.maxHealth ?? entity.maxHealth;
            if (entity.entityType === EntityTypes.HERO) {
                EventBus.emit(Events.HERO_HEALTH_CHANGE, { current: healthComponent.health, max: maxH });
            } else {
                EventBus.emit(Events.ENTITY_HEALTH_CHANGE, { entity, current: healthComponent.health, max: maxH });
            }
        } else if (isDamageable(entity)) {
            // Entity handles its own damage (e.g. Resource)
            Logger.info(`[DamageSystem] Delegating damage to entity ${entity.id}`);
            entity.takeDamage(finalDamage);
            return; // Entity handles VFX/Death/State
        } else if (typeof entity.health === 'number') {
            Logger.info(
                `[DamageSystem] Applying ${finalDamage} damage directly to entity ${entity.id}`
            );
            // Fallback to direct entity property (Enemies mostly)
            entity.health -= finalDamage;
            if (entity.health < 0) entity.health = 0;
            tookDamage = true;
        }

        if (!tookDamage) return;

        // 2. Trigger VFX & SFX
        this.playHitEffects(entity, finalDamage, type);

        // 3. Check Death
        const currentHealth = healthComponent ? healthComponent.health : entity.health;
        if (currentHealth <= 0) {
            // Call entity-specific death handler (sets respawn timers, cleanup, etc.)
            if (isMortal(entity)) {
                entity.die(source);
            } else {
                // Fallback
                entity.isDead = true;
                entity.state = 'dead';
            }

            EventBus.emit(GameConstants.Events.ENTITY_DIED, {
                entity: entity,
                killer: source
            });
        }
    }

    /**
     * visual and audio effects for damage
     */
    playHitEffects(entity: IEntity, amount: number, type: string = 'physical') {
        const x = entity.x;
        const y = entity.y;

        // Floating Text: emit event; FloatingTextManager subscribes
        const yOffset = GameConstants.Damage.FLOATING_TEXT_Y_OFFSET;
        const isCrit = type === 'crit';
        if (EventBus) {
            EventBus.emit(GameConstants.Events.DAMAGE_NUMBER_REQUESTED, {
                x,
                y: y - yOffset,
                amount: Math.round(amount),
                isCrit
            });
        }

        // SFX
        if (AudioManager) {
            const isHero = entity.entityType === EntityTypes.HERO;
            AudioManager.playSFX(isHero ? 'sfx_hero_hurt' : 'sfx_enemy_hurt');
        }

        // VFX (Blood)
        if (VFXController && VFXConfig) {
            const isMechanical = entity.entityType === EntityTypes.BUILDING; // Assuming buildings bleed sparks not blood

            if (isMechanical) {
                // Sparks for mechanical/buildings
                // TODO: Add Spark VFX config
            } else {
                // Organic Blood VFX
                VFXController.playForeground(x, y, VFXConfig.DINO.BLOOD_SPLATTER);
                VFXController.playForeground(x, y, VFXConfig.DINO.BLOOD_MIST);

                const threshold = GameConstants.Combat.DAMAGE_VFX_THRESHOLD;
                if (amount > threshold) {
                    VFXController.playForeground(x, y, VFXConfig.DINO.BLOOD_DROPS);
                }
            }
        }
    }

    /**
     * Handle entity death
     */
    handleDeath(data: { entity: IEntity; killer?: IEntity }) {
        const { entity, killer } = data;

        Logger.info(`[DamageSystem] Entity died: ${entity.entityType} (ID: ${entity.id})`);

        // 1. Hero Death
        if (entity.entityType === EntityTypes.HERO) {
            EventBus.emit(GameConstants.Events.HERO_DIED, { hero: entity });
            if (AudioManager) AudioManager.playSFX('sfx_hero_death');
            return;
        }

        // 2. Enemy Death
        if (this.isEnemy(entity)) {
            if (AudioManager) AudioManager.playSFX('sfx_enemy_death');

            // Emit kill event for Quests/XP
            EventBus.emit(GameConstants.Events.ENEMY_KILLED, {
                enemy: entity,
                xpReward: entity.xpReward ?? GameConstants.Combat.XP_REWARD_FALLBACK,
                lootTableId: entity.lootTableId
            });
        }
    }

    isEnemy(entity: IEntity): boolean {
        return (
            entity.entityType === EntityTypes.ENEMY_DINOSAUR ||
            entity.entityType === EntityTypes.ENEMY_SOLDIER ||
            entity.entityType === EntityTypes.ENEMY_SAURIAN ||
            (entity.constructor && entity.constructor.name === 'Enemy')
        );
    }
}

