import { createNoise2D } from 'simplex-noise';
import FlatQueue from 'flatqueue';
import { makeRandFloat } from './Prng';
import { clamp } from './geometry';
import type { Mesh } from './types';

interface PrecalculatedNoise {
    noise0_t: Float32Array;
    noise1_t: Float32Array;
    noise2_t: Float32Array;
    noise4_t: Float32Array;
    noise5_t: Float32Array;
    noise6_t: Float32Array;
}

const mountain = { slope: 16 };

function calculateMountainDistance(
    mesh: Mesh,
    t_peaks: number[],
    spacing: number,
    jaggedness: number,
    randFloat: () => number,
    distance_t: Float32Array
): void {
    distance_t.fill(-1);
    const t_queue = t_peaks.slice();
    for (let i = 0; i < t_queue.length; i++) {
        const t_current = t_queue[i];
        for (let j = 0; j < 3; j++) {
            const s = 3 * t_current + j;
            const t_neighbor = mesh.t_outer_s(s);
            if (distance_t[t_neighbor] === -1) {
                const increment = spacing * (1 + jaggedness * (randFloat() - randFloat()));
                distance_t[t_neighbor] = distance_t[t_current] + increment;
                t_queue.push(t_neighbor);
            }
        }
    }
}

function precalculateNoise(randFloat: () => number, mesh: Mesh): PrecalculatedNoise {
    const noise2D = createNoise2D(randFloat);
    const { numTriangles } = mesh;
    const noise0_t = new Float32Array(numTriangles);
    const noise1_t = new Float32Array(numTriangles);
    const noise2_t = new Float32Array(numTriangles);
    const noise4_t = new Float32Array(numTriangles);
    const noise5_t = new Float32Array(numTriangles);
    const noise6_t = new Float32Array(numTriangles);
    for (let t = 0; t < numTriangles; t++) {
        const nx = (mesh.x_of_t(t) - 500) / 500;
        const ny = (mesh.y_of_t(t) - 500) / 500;
        noise0_t[t] = noise2D(nx, ny);
        noise1_t[t] = noise2D(2 * nx + 5, 2 * ny + 5);
        noise2_t[t] = noise2D(4 * nx + 7, 4 * ny + 7);
        noise4_t[t] = noise2D(16 * nx + 15, 16 * ny + 15);
        noise5_t[t] = noise2D(32 * nx + 31, 32 * ny + 31);
        noise6_t[t] = noise2D(64 * nx + 67, 64 * ny + 67);
    }
    return { noise0_t, noise1_t, noise2_t, noise4_t, noise5_t, noise6_t };
}

export interface ElevationParam {
    seed: number;
    island: number;
    noisy_coastlines: number;
    hill_height: number;
    mountain_jagged: number;
    mountain_sharpness: number;
    mountain_folds: number;
    ocean_depth: number;
}

export interface BiomesParam {
    wind_angle_deg: number;
    raininess: number;
    rain_shadow: number;
    evaporation: number;
}

export interface RiversParam {
    lg_min_flow: number;
    lg_river_width: number;
    flow: number;
}

export interface MapConstraints {
    size: number;
    constraints: Float32Array;
}

export default class Mapgen4Map {
    seed = -1;
    spacing!: number;
    precomputed!: PrecalculatedNoise;
    mountainJaggedness = -Infinity;
    windAngleDeg = Infinity;
    elevation_t!: Float32Array;
    elevation_r!: Float32Array;
    humidity_r!: Float32Array;
    moisture_t!: Float32Array;
    rainfall_r!: Float32Array;
    s_downslope_t!: Int32Array;
    t_order!: Int32Array;
    flow_t!: Float32Array;
    flow_s!: Float32Array;
    r_wind_order!: Int32Array;
    wind_sort_r!: Float32Array;
    mountain_distance_t!: Float32Array;

    constructor(
        public mesh: Mesh,
        public t_peaks: number[],
        param: { spacing: number }
    ) {
        this.spacing = param.spacing;
        this.elevation_t = new Float32Array(mesh.numTriangles);
        this.elevation_r = new Float32Array(mesh.numRegions);
        this.humidity_r = new Float32Array(mesh.numRegions);
        this.moisture_t = new Float32Array(mesh.numTriangles);
        this.rainfall_r = new Float32Array(mesh.numRegions);
        this.s_downslope_t = new Int32Array(mesh.numTriangles);
        this.t_order = new Int32Array(mesh.numTriangles);
        this.flow_t = new Float32Array(mesh.numTriangles);
        this.flow_s = new Float32Array(mesh.numSides);
        this.r_wind_order = new Int32Array(mesh.numRegions);
        this.wind_sort_r = new Float32Array(mesh.numRegions);
        this.mountain_distance_t = new Float32Array(mesh.numTriangles);
    }

    assignTriangleElevation(elevationParam: ElevationParam, constraints: MapConstraints): void {
        const { mesh, elevation_t, mountain_distance_t, precomputed } = this;
        const { numTriangles, numSolidTriangles } = mesh;
        const C = constraints.constraints;
        const size = constraints.size;

        function constraintAt(x: number, y: number): number {
            x = clamp(x * (size - 1), 0, size - 2);
            y = clamp(y * (size - 1), 0, size - 2);
            const xInt = Math.floor(x);
            const yInt = Math.floor(y);
            const xFrac = x - xInt;
            const yFrac = y - yInt;
            const p = size * yInt + xInt;
            const e00 = C[p];
            const e01 = C[p + 1];
            const e10 = C[p + size];
            const e11 = C[p + size + 1];
            return (
                (e00 * (1 - xFrac) + e01 * xFrac) * (1 - yFrac) +
                (e10 * (1 - xFrac) + e11 * xFrac) * yFrac
            );
        }

        for (let t = 0; t < numSolidTriangles; t++) {
            let e = constraintAt(mesh.x_of_t(t) / 1000, mesh.y_of_t(t) / 1000);
            e +=
                elevationParam.noisy_coastlines *
                (1 - e * e * e * e) *
                (precomputed.noise4_t[t] +
                    precomputed.noise5_t[t] / 2 +
                    precomputed.noise6_t[t] / 4);
            this.elevation_t[t] = e;
        }

        const mountain_slope = mountain.slope;
        const mountain_sharpness = Math.pow(2, elevationParam.mountain_sharpness);
        const { noise0_t, noise1_t, noise2_t, noise4_t } = precomputed;
        for (let t = 0; t < numTriangles; t++) {
            let e = this.elevation_t[t];
            if (e > 0) {
                const noisiness = 1.0 - 0.5 * (1 + noise0_t[t]);
                let eh =
                    (1 + noisiness * noise4_t[t] + (1 - noisiness) * noise2_t[t]) *
                    elevationParam.hill_height;
                if (eh < 0.01) eh = 0.01;
                let em = 1 - (mountain_slope / mountain_sharpness) * mountain_distance_t[t];
                if (em < 0.01) em = 0.01;
                const weight = e * e;
                e = (1 - weight) * eh + weight * em;
            } else {
                e *= elevationParam.ocean_depth + noise1_t[t];
            }
            if (e < -1.0) e = -1.0;
            if (e > 1.0) e = 1.0;
            this.elevation_t[t] = e;
        }
    }

    assignRegionElevation(): void {
        const { mesh, elevation_t, elevation_r } = this;
        const { _s_of_r, _halfedges } = mesh;
        for (let r = 0; r < mesh.numRegions; r++) {
            let count = 0;
            let e = 0;
            let water = false;
            let s_incoming = _s_of_r[r];
            const s0 = s_incoming;
            do {
                const t = (s_incoming / 3) | 0;
                e += elevation_t[t];
                water = water || elevation_t[t] < 0.0;
                const s_outgoing = mesh.s_next_s(s_incoming);
                s_incoming = _halfedges[s_outgoing];
                count++;
            } while (s_incoming !== s0);
            e /= count;
            if (water && e >= 0) e = -0.001;
            elevation_r[r] = e;
        }
    }

    assignElevation(elevationParam: ElevationParam, constraints: MapConstraints): void {
        if (
            this.seed !== elevationParam.seed ||
            this.mountainJaggedness !== elevationParam.mountain_jagged
        ) {
            this.mountainJaggedness = elevationParam.mountain_jagged;
            calculateMountainDistance(
                this.mesh,
                this.t_peaks,
                this.spacing,
                this.mountainJaggedness,
                makeRandFloat(elevationParam.seed),
                this.mountain_distance_t
            );
        }
        if (this.seed !== elevationParam.seed) {
            this.seed = elevationParam.seed;
            this.precomputed = precalculateNoise(makeRandFloat(elevationParam.seed), this.mesh);
        }
        this.assignTriangleElevation(elevationParam, constraints);
        this.assignRegionElevation();
    }

    assignRainfall(biomesParam: BiomesParam): void {
        const { mesh, r_wind_order, wind_sort_r, humidity_r, rainfall_r, elevation_r } = this;
        const { _s_of_r, _halfedges } = mesh;

        if (biomesParam.wind_angle_deg !== this.windAngleDeg) {
            this.windAngleDeg = biomesParam.wind_angle_deg;
            const windAngleRad = (Math.PI / 180) * this.windAngleDeg;
            const windAngleVec = [Math.cos(windAngleRad), Math.sin(windAngleRad)];
            for (let r = 0; r < mesh.numRegions; r++) {
                r_wind_order[r] = r;
                wind_sort_r[r] =
                    mesh.x_of_r(r) * windAngleVec[0] + mesh.y_of_r(r) * windAngleVec[1];
            }
            r_wind_order.sort((r1, r2) => wind_sort_r[r1] - wind_sort_r[r2]);
        }

        for (let i = 0; i < r_wind_order.length; i++) {
            const r = r_wind_order[i];
            let count = 0;
            let sum = 0.0;
            let s_incoming = _s_of_r[r];
            const s0 = s_incoming;
            do {
                const r_neighbor = mesh.r_begin_s(s_incoming);
                if (wind_sort_r[r_neighbor] < wind_sort_r[r]) {
                    count++;
                    sum += humidity_r[r_neighbor];
                }
                const s_outgoing = mesh.s_next_s(s_incoming);
                s_incoming = _halfedges[s_outgoing];
            } while (s_incoming !== s0);

            let humidity = 0.0;
            let rainfall = 0.0;
            if (count > 0) {
                humidity = sum / count;
                rainfall += biomesParam.raininess * humidity;
            }
            if (mesh.is_boundary_r(r)) humidity = 1.0;
            if (elevation_r[r] < 0.0) {
                const evaporation = biomesParam.evaporation * -elevation_r[r];
                humidity += evaporation;
            }
            if (humidity > 1.0 - elevation_r[r]) {
                const orographicRainfall =
                    biomesParam.rain_shadow * (humidity - (1.0 - elevation_r[r]));
                rainfall += biomesParam.raininess * orographicRainfall;
                humidity -= orographicRainfall;
            }
            rainfall_r[r] = rainfall;
            humidity_r[r] = humidity;
        }
    }

    assignRivers(riversParam: RiversParam): void {
        const {
            mesh,
            moisture_t,
            rainfall_r,
            elevation_t,
            s_downslope_t,
            t_order,
            flow_t,
            flow_s
        } = this;
        assignDownslope(mesh, elevation_t, s_downslope_t, t_order);
        assignMoisture(mesh, rainfall_r, moisture_t);
        assignFlow(
            mesh,
            riversParam,
            t_order,
            elevation_t,
            moisture_t,
            s_downslope_t,
            flow_t,
            flow_s
        );
    }
}

const queue = new FlatQueue<number>();

function assignDownslope(
    mesh: Mesh,
    elevation_t: Float32Array,
    s_downslope_t: Int32Array,
    t_order: Int32Array
): void {
    const { numTriangles } = mesh;
    let queue_in = 0;
    s_downslope_t.fill(-999);
    for (let t = 0; t < numTriangles; t++) {
        if (elevation_t[t] < -0.1) {
            let s_best = -1;
            let e_best = elevation_t[t];
            for (let j = 0; j < 3; j++) {
                const s = 3 * t + j;
                const e = elevation_t[mesh.t_outer_s(s)];
                if (e < e_best) {
                    e_best = e;
                    s_best = s;
                }
            }
            t_order[queue_in++] = t;
            s_downslope_t[t] = s_best;
            queue.push(t, elevation_t[t]);
        }
    }
    for (let queue_out = 0; queue_out < numTriangles; queue_out++) {
        const t_current = queue.pop()!;
        for (let j = 0; j < 3; j++) {
            const s = 3 * t_current + j;
            const t_neighbor = mesh.t_outer_s(s);
            if (s_downslope_t[t_neighbor] === -999) {
                s_downslope_t[t_neighbor] = mesh.s_opposite_s(s);
                t_order[queue_in++] = t_neighbor;
                queue.push(t_neighbor, elevation_t[t_neighbor]);
            }
        }
    }
}

function assignMoisture(mesh: Mesh, rainfall_r: Float32Array, moisture_t: Float32Array): void {
    const { numTriangles } = mesh;
    for (let t = 0; t < numTriangles; t++) {
        let moisture = 0.0;
        for (let i = 0; i < 3; i++) {
            const s = 3 * t + i;
            const r = mesh.r_begin_s(s);
            moisture += rainfall_r[r] / 3;
        }
        moisture_t[t] = moisture;
    }
}

function assignFlow(
    mesh: Mesh,
    riversParam: RiversParam,
    t_order: Int32Array,
    elevation_t: Float32Array,
    moisture_t: Float32Array,
    s_downslope_t: Int32Array,
    flow_t: Float32Array,
    flow_s: Float32Array
): void {
    const { numTriangles, _halfedges } = mesh;
    flow_s.fill(0);
    for (let t = 0; t < numTriangles; t++) {
        if (elevation_t[t] >= 0.0) {
            flow_t[t] = riversParam.flow * moisture_t[t] * moisture_t[t];
        } else {
            flow_t[t] = 0;
        }
    }
    for (let i = t_order.length - 1; i >= 0; i--) {
        const t_tributary = t_order[i];
        const s_flow = s_downslope_t[t_tributary];
        if (s_flow >= 0) {
            const t_trunk = (_halfedges[s_flow] / 3) | 0;
            flow_t[t_trunk] += flow_t[t_tributary];
            flow_s[s_flow] += flow_t[t_tributary];
            if (
                elevation_t[t_trunk] > elevation_t[t_tributary] &&
                elevation_t[t_tributary] >= 0.0
            ) {
                elevation_t[t_trunk] = elevation_t[t_tributary];
            }
        }
    }
}

/**
 * EquipmentUI - Fullscreen equipment management screen (Core)
 *
 * Delegates to:
 * - EquipmentUIRenderer: All HTML/template rendering
 * - EquipmentSlotManager: Equip/unequip/slot logic
 * - EquipmentUIFilterConfig: Filter hierarchy
 * - EquipmentUIFooter: Footer swap/restore
 * - EquipmentUIClickHandler: Event delegation
 */
import { GameConstants } from '@data/GameConstants';
import { Logger } from '@core/Logger';
import { EquipmentSlotManager } from './EquipmentSlotManager';
import { EntityLoader } from '@entities/EntityLoader';
import { EquipmentUIRenderer } from './EquipmentUIRenderer';
import { Registry } from '@core/Registry';
import { DOMUtils } from '@core/DOMUtils';
import { GameInstance } from '@core/Game';
// import removed
import { MODE_CATEGORIES } from './EquipmentUIFilterConfig';
import { getFilterHierarchy } from './EquipmentUIFilterConfig';
import { WeaponWheelInstance } from './WeaponWheel';
import { swapFooterToEquipmentMode, restoreFooterButtons } from './EquipmentUIFooter';
import { handleEquipmentUIClick } from './EquipmentUIClickHandler';
import { renderItemStats } from './EquipmentUIRendererStats';
import type { EquipmentItem, IFooterConfig } from '../types/ui';

export interface FilterCategory {
    id: string;
    label: string;
    iconId?: string;
    children?: FilterCategory[];
}

class EquipmentUI {
    isOpen: boolean;
    selectedMode: string;
    selectedCategory: string;
    selectedItem: EquipmentItem | null;
    container: HTMLElement | null;
    cachedEquipment: EquipmentItem[];
    lastClickedItemId: string | null;
    lastClickTime: number;
    slotSelectionMode: boolean;
    pendingEquipItem: EquipmentItem | null;
    targetEquipSet: number;
    originalFooterConfigs: IFooterConfig | null = null;
    slots: string[];
    toolSlots: string[];
    modeCategories: Record<string, FilterCategory[]>;

    constructor() {
        this.isOpen = false;
        this.selectedMode = 'armor';
        this.selectedCategory = 'all';
        this.selectedItem = null;
        this.container = null;
        this.cachedEquipment = [];
        this.lastClickedItemId = null;
        this.lastClickTime = 0;
        this.slotSelectionMode = false;
        this.pendingEquipItem = null;
        this.targetEquipSet = 1;
        this.originalFooterConfigs = null;

        const equipCfg = GameConstants?.Equipment || {
            ALL_SLOTS: [] as string[],
            TOOL_SLOTS: [] as string[]
        };
        this.slots = equipCfg.ALL_SLOTS || [
            'head',
            'body',
            'hands',
            'legs',
            'accessory',
            'hand1',
            'hand2',
            'accessory2'
        ];
        this.toolSlots = equipCfg.TOOL_SLOTS || [
            'tool_mining',
            'tool_woodcutting',
            'tool_harvesting',
            'tool_fishing'
        ];
        this.modeCategories = MODE_CATEGORIES;

        if (document.readyState === 'loading') {
            document.addEventListener('DOMContentLoaded', () => this._init());
        } else {
            this._init();
        }
    }

    _init() {
        const btnEquip = document.getElementById('btn-equip');
        if (btnEquip) {
            btnEquip.addEventListener('click', () => {
                if (btnEquip.dataset.footerOverride) return;
                this.toggle();
            });
        }

        this._createContainer();

        const uiMgr = Registry.get('UIManager') as any;
        if (uiMgr && uiMgr.registerFullscreenUI) {
            uiMgr.registerFullscreenUI(this);
            Logger.info('[EquipmentUI] Registered with UIManager');
        } else {
            Logger.error('[EquipmentUI] Failed to register with UIManager');
        }

        Logger.info('[EquipmentUI] Initialized');
    }

    _createContainer() {
        this.container = DOMUtils.create('div', {
            id: 'equipment-screen',
            className: 'equipment-screen',
            styles: { display: 'none' }
        });

        this.container.addEventListener('click', (e) => handleEquipmentUIClick(this, e));
        this.container.addEventListener('dblclick', (e) => {
            const invItem = (e.target as HTMLElement).closest('.inventory-item');
            if ((invItem as HTMLElement)?.dataset.id) {
                EquipmentSlotManager.equipItem(this, (invItem as HTMLElement).dataset.id!);
            }
        });

        const uiOverlay = document.getElementById('ui-overlay');
        (uiOverlay || document.body).appendChild(this.container);
    }

    toggle() {
        if (this.isOpen) {
            this.close();
        } else {
            this.open();
        }
    }

    open() {
        WeaponWheelInstance.close();

        const uiMgr = Registry.get('UIManager') as any;
        if (uiMgr && uiMgr.closeOtherFullscreenUIs) {
            uiMgr.closeOtherFullscreenUIs(this);
        }

        const hero = GameInstance?.hero;
        if (hero?.equipment?.activeWeaponSet) {
            this.targetEquipSet = hero.equipment.activeWeaponSet;
        }

        swapFooterToEquipmentMode(this);
        this.isOpen = true;
        this._loadEquipment();
        this._render();
        this.container!.style.display = 'flex';
        Logger.info('[EquipmentUI] Opened');
    }

    close() {
        this.isOpen = false;
        this.container!.style.display = 'none';
        restoreFooterButtons(this);
        Logger.info('[EquipmentUI] Closed');
    }

    _getFilterHierarchy(): FilterCategory[] {
        return getFilterHierarchy(this.selectedMode);
    }

    _updateFooterActiveStates() {
        const btnInventory = document.getElementById('btn-inventory');
        const btnEquip = document.getElementById('btn-equip');
        const btnMap = document.getElementById('btn-map');
        btnInventory?.classList.toggle('active', this.selectedMode === 'armor');
        btnEquip?.classList.toggle('active', this.selectedMode === 'weapon');
        btnMap?.classList.toggle('active', this.selectedMode === 'tool');
    }

    _loadEquipment() {
        const allEquipment = (EntityLoader?.getAllEquipment?.() as unknown as EquipmentItem[]) || [];
        const filterParts = this.selectedCategory?.split(':') || ['all'];
        const grip = filterParts[0];
        const weaponType = filterParts[1];
        const subtype = filterParts[2];

        if (this.selectedMode === 'armor') {
            this.cachedEquipment = allEquipment.filter(
                (e) =>
                    grip === 'all' ||
                    e.sourceFile === grip ||
                    e.slot === grip ||
                    e.equipSlot === grip
            );
        } else if (this.selectedMode === 'weapon') {
            this.cachedEquipment = allEquipment.filter((e) => {
                const isWeapon =
                    e.weaponType !== undefined || e.sourceFile === 'weapon' || e.slot === 'weapon';
                if (!isWeapon) return false;
                if (grip !== 'all' && e.gripType !== grip) return false;
                if (weaponType) {
                    const isShieldAsMelee = weaponType === 'melee' && e.weaponType === 'shield';
                    if (!isShieldAsMelee && e.weaponType !== weaponType) return false;
                }
                if (subtype && e.weaponSubtype !== subtype && !e.id.includes(subtype)) return false;
                return true;
            });
        } else if (this.selectedMode === 'tool') {
            this.cachedEquipment = allEquipment.filter((e) => {
                const isTool = e.sourceFile === 'tool' || e.slot === 'tool';
                if (!isTool) return false;
                return grip === 'all' || e.id.includes(grip);
            });
        } else {
            this.cachedEquipment = allEquipment;
        }
    }

    _render() {
        this.container!.innerHTML = EquipmentUIRenderer.renderPanel(this);
        EquipmentUIRenderer.loadIcons(this.container!);
    }

    _selectItemNoRender(itemId: string) {
        const item = this.cachedEquipment.find((e) => e.id === itemId);
        this.selectedItem = item || null;

        this.container!.querySelectorAll('.inventory-item').forEach((el) => {
            el.classList.toggle('selected', (el as HTMLElement).dataset.id === itemId);
        });

        const nameEl = this.container!.querySelector('.item-preview-name');
        const statsRow = this.container!.querySelector('.item-stats-row');
        if (item && nameEl) {
            nameEl.textContent = item.name;
            if (statsRow) statsRow.innerHTML = renderItemStats(item);
            this.container!.querySelector('.equip-summary-bar')?.classList.add('has-item');
            this.container!.querySelector('.equip-summary-bar')?.classList.remove('empty');
        }
    }

    _selectItem(itemId: string) {
        const item = this.cachedEquipment.find((e) => e.id === itemId);
        this.selectedItem = item || null;
        this._render();
    }

    _selectEquippedSlot(slotId: string) {
        const hero = GameInstance?.hero;
        const equippedItem = hero?.equipment?.getSlot(slotId) as EquipmentItem | undefined;

        if (equippedItem) {
            this.selectedItem = equippedItem;

            this.container!.querySelectorAll('.equip-slot').forEach((el) => {
                el.classList.toggle('selected', (el as HTMLElement).dataset.slot === slotId);
            });

            this.container!.querySelectorAll('.inventory-item').forEach((el) => {
                el.classList.remove('selected');
            });

            const nameEl = this.container!.querySelector('.item-preview-name');
            const statsRow = this.container!.querySelector('.item-stats-row');
            if (nameEl) {
                nameEl.textContent = equippedItem.name;
                if (statsRow)
                    statsRow.innerHTML = renderItemStats(equippedItem);
                this.container!.querySelector('.equip-summary-bar')?.classList.add('has-item');
                this.container!.querySelector('.equip-summary-bar')?.classList.remove('empty');
            }
        }
    }
}

const equipmentUIInstance = new EquipmentUI();
if (Registry) Registry.register('EquipmentUI', equipmentUIInstance);

export { EquipmentUI, equipmentUIInstance };

/**
 * MinimapRenderer  Draw minimap content (mapgen4 terrain, entities, hero).
 */
import { Registry } from '@core/Registry';
import { entityManager } from '@core/EntityManager';
import { GameConstants } from '@data/GameConstants';
import { generateMapgen4 } from '../tools/map-editor/Mapgen4Generator';
import { drawCachedMeshToCanvas } from '../tools/map-editor/Mapgen4PreviewRenderer';
import type { Mapgen4Param } from '../tools/map-editor/Mapgen4Param';

const WORLD_SIZE = 160000;
const MESH_SIZE = 1000;

export function renderMinimap(
    ctx: CanvasRenderingContext2D,
    canvasSize: number,
    scale: number,
    viewLeft: number,
    viewTop: number,
    viewWidth: number,
    viewHeight: number,
    hero: { x: number; y: number } | null,
    zoomLevel: number
): void {
    const toCanvas = (worldX: number, worldY: number) => ({
        x: (worldX - viewLeft) * scale,
        y: (worldY - viewTop) * scale
    });

    // Draw mapgen4 polygon map as base terrain
    const worldManager = Registry?.get<{
        getMesh: () => { mesh: unknown; map: unknown } | null;
        getMapgen4Param: () => unknown;
        getCachedTownsAndRoads?: () => {
            towns: unknown[];
            roadSegments: unknown[];
            railroadPath: number[];
            railroadCrossings: unknown[];
        };
    }>('WorldManager');
    const meshAndMap = worldManager?.getMesh?.();
    if (meshAndMap) {
        const param = worldManager.getMapgen4Param();
        const { mesh, map } = meshAndMap;
        const { towns = [], roadSegments = [], railroadPath = [], railroadCrossings = [], railroadStationIds = [] } =
            worldManager.getCachedTownsAndRoads?.() ?? {};
        const vpX = (viewLeft / WORLD_SIZE) * MESH_SIZE;
        const vpY = (viewTop / WORLD_SIZE) * MESH_SIZE;
        const vpW = (viewWidth / WORLD_SIZE) * MESH_SIZE;
        const vpH = (viewHeight / WORLD_SIZE) * MESH_SIZE;
        const canvas = ctx.canvas;
        if (canvas) {
            drawCachedMeshToCanvas(
                canvas,
                mesh,
                map,
                param,
                vpX,
                vpY,
                vpW,
                vpH,
                towns,
                roadSegments,
                railroadPath,
                railroadCrossings,
                railroadStationIds
            );
        }
    } else {
        ctx.fillStyle = '#0d1117';
        ctx.fillRect(0, 0, canvasSize, canvasSize);
    }

    if (entityManager) {
        ctx.fillStyle = '#F44336';
        for (const enemy of entityManager.getByType('Enemy')) {
            if (!enemy.active || enemy.isDead) continue;
            const pos = toCanvas(enemy.x, enemy.y);
            if (pos.x < 0 || pos.x > canvasSize || pos.y < 0 || pos.y > canvasSize) continue;
            ctx.beginPath();
            ctx.arc(pos.x, pos.y, 4, 0, Math.PI * 2);
            ctx.fill();
        }
        for (const boss of entityManager.getByType('Boss')) {
            if (!boss.active || boss.isDead) continue;
            const pos = toCanvas(boss.x, boss.y);
            if (pos.x < 0 || pos.x > canvasSize || pos.y < 0 || pos.y > canvasSize) continue;
            const pulse = Math.sin(Date.now() / 150) * 0.3 + 1;
            ctx.fillStyle = 'rgba(255, 69, 0, 0.5)';
            ctx.beginPath();
            ctx.arc(pos.x, pos.y, 10 * pulse, 0, Math.PI * 2);
            ctx.fill();
            ctx.fillStyle = '#FF4500';
            ctx.beginPath();
            ctx.arc(pos.x, pos.y, 6, 0, Math.PI * 2);
            ctx.fill();
            ctx.fillStyle = '#FFF';
            ctx.font = 'bold 10px sans-serif';
            ctx.textAlign = 'center';
            ctx.textBaseline = 'middle';
            ctx.fillText('', pos.x, pos.y);
        }
    }

    // Hero: always at center (view scrolls with hero)
    if (hero) {
        const centerX = canvasSize / 2;
        const centerY = canvasSize / 2;
        const pulse = Math.sin(Date.now() / GameConstants.UI.MINIMAP_PULSE_MS) * 0.3 + 1;
        ctx.fillStyle = 'rgba(255, 87, 34, 0.4)';
        ctx.beginPath();
        ctx.arc(centerX, centerY, 12 * pulse, 0, Math.PI * 2);
        ctx.fill();
        ctx.save();
        ctx.translate(centerX, centerY);
        ctx.fillStyle = '#FF5722';
        ctx.beginPath();
        ctx.moveTo(0, -8);
        ctx.lineTo(6, 8);
        ctx.lineTo(-6, 8);
        ctx.closePath();
        ctx.fill();
        ctx.restore();
    }

    ctx.fillStyle = 'rgba(255, 255, 255, 0.6)';
    ctx.font = '10px sans-serif';
    ctx.textAlign = 'right';
    ctx.textBaseline = 'bottom';
    ctx.fillText(`Zoom: ${zoomLevel.toFixed(1)}x`, canvasSize - 10, canvasSize - 10);
}

/**
 * UI Manager
 * Orchestrates UI rendering and updates
 *
 * REFACTOR NOTICE: Split into focused files:
 * - ContextActionUI.js: Context-sensitive action button
 * - UICapture.js: Debug screenshot utilities
 *
 * Owner: UI Artist
 */

import { Logger } from '@core/Logger';
import { PlatformManager } from '@core/PlatformManager';
import { ResponsiveManager } from '@core/ResponsiveManager';
import { AudioManager } from '../audio/AudioManager';
import { ContextActionUI } from './ContextActionUI';
import { GameConstants } from '@data/GameConstants';
import { EventBus } from '@core/EventBus';
import { VFXController } from '@vfx/VFXController';
import { AssetLoader } from '@core/AssetLoader';
import { Registry } from '@core/Registry';
import { DOMUtils } from '@core/DOMUtils';
import { UICapture } from './UICapture';
import { GameInstance } from '@core/Game';
import { LayoutStrategies } from './responsive/LayoutStrategies';
import { HUDController } from './controllers/HUDController';
import type { IUIPanel, IQuest } from '../types/ui';

interface IFullscreenUI {
    isOpen: boolean;
    close(): void;
}

class UIManagerService {
    initialized: boolean = false;
    panels: Map<string, IUIPanel> = new Map();
    fullscreenUIs: Set<IFullscreenUI> = new Set();
    currentStrategy: {
        apply?(container: HTMLElement): void;
        enter?(): void;
        exit?(): void;
    } | null = null;
    isFooterOverridden: boolean = false;

    constructor() {
        Logger.debug('[UIManager]', 'Constructed');
    }

    init() {
        Logger.info('[UIManager]', 'Initializing...');
        // Ensure HUDController is loaded
        if (HUDController) Logger.debug('[UIManager] HUDController loaded');

        // Listen for platform changes
        if (PlatformManager) {
            PlatformManager.on('modechange', (config: unknown) => this.onPlatformChange(config));
            this.onPlatformChange(PlatformManager.getConfig());
        } else if (ResponsiveManager) {
            ResponsiveManager.on('change', (data: unknown) =>
                this.onResponsiveChange(
                    data as { format: string; orientation?: string; breakpoint?: string }
                )
            );
        }

        this.loadIcons();

        // Global click sound
        document.addEventListener('click', (e) => {
            const target = e.target as HTMLElement;
            if (
                target.matches('button, .clickable, .icon-btn, .shop-item, .upgrade-card') ||
                target.closest('button, .clickable, .icon-btn, .shop-item, .upgrade-card')
            ) {
                if (AudioManager) AudioManager.playSFX('sfx_ui_click');
            }

            // Action button click animation - add class so animation plays fully
            const actionBtn = target.closest('.action-btn') as HTMLElement | null;
            if (actionBtn && !actionBtn.classList.contains('clicked')) {
                actionBtn.classList.add('clicked');
                setTimeout(() => actionBtn.classList.remove('clicked'), 350);
            }
        });

        // Escape to exit Capture Mode
        document.addEventListener('keydown', (e) => {
            if (e.key === 'Escape' && document.body.classList.contains('ui-capture-mode')) {
                if (UICapture) UICapture.toggleMode();
            }
        });

        // Init ContextActionUI
        if (ContextActionUI) ContextActionUI.init();

        // Magnet Button
        const btnMagnet = document.getElementById('btn-magnet');
        if (btnMagnet) {
            btnMagnet.addEventListener('click', () => {
                // Skip if footer is in override mode (equipment/inventory screen has taken over)
                if (this.isFooterOverridden) return;
                const eventName =
                    GameConstants && GameConstants.Events
                        ? GameConstants.Events.REQUEST_MAGNET
                        : 'REQUEST_MAGNET';
                if (EventBus) EventBus.emit(eventName);
            });
        }

        // Weapon Set Swap Button
        const btnSwap = document.getElementById('btn-weapon-swap');
        if (btnSwap) {
            btnSwap.addEventListener('click', () => {
                const hero = GameInstance?.hero;
                if (hero?.equipment?.swapWeaponSet) {
                    const activeSet = hero.equipment.swapWeaponSet();
                    Logger.info(`[UIManager] Weapon set swapped to Set ${activeSet}`);
                    if (AudioManager) AudioManager.playSFX('sfx_ui_click');
                }
            });
        }

        // EventBus Listeners
        if (EventBus && GameConstants) {
            const E = GameConstants.Events;
            EventBus.on(E.UI_FADE_SCREEN, (data: { onMidpoint?: () => void } | null) =>
                this.fadeInOut(data ? data.onMidpoint : null)
            );
            EventBus.on(E.QUEST_UPDATED, (data: { quest: IQuest | null; animate?: boolean }) =>
                this.updateQuest(data.quest ?? null, data.animate ?? false)
            );
            EventBus.on(E.UI_FULLSCREEN_OPENED, (data: { source: IFullscreenUI } | null) => {
                if (data?.source) this.closeOtherFullscreenUIs(data.source);
            });

            // Track when main UI panels override the footer
            EventBus.on('UI_PANEL_OPENED', (data: { panelId: string }) => {
                // Determine if this panel should override the footer (e.g., inventory/equipment)
                if (data && ['inventory', 'equipment', 'crafting'].includes(data.panelId)) {
                    this.isFooterOverridden = true;
                }
            });
            EventBus.on('UI_PANEL_CLOSED', (data: { panelId: string }) => {
                if (data && ['inventory', 'equipment', 'crafting'].includes(data.panelId)) {
                    this.isFooterOverridden = false;
                }
            });
        }

        this.initialized = true;
        Logger.info('[UIManager]', 'Initialized');
    }

    // === Icon Loading ===
    loadIcons() {
        if (!AssetLoader) return;

        const elements = document.querySelectorAll('[data-icon-id]');
        Logger.debug('[UIManager]', `Loading icons for ${elements.length} elements`);

        elements.forEach((element) => {
            const el = element as HTMLElement;
            const id = el.dataset.iconId;
            const path = AssetLoader.getImagePath(id);
            if (path) {
                el.style.backgroundImage = `url('${path}')`;
                el.style.backgroundSize = 'cover';
                el.style.backgroundPosition = 'center';
            }
        });
    }

    // === Platform/Layout ===
    onPlatformChange(_config: unknown) {
        this.applyLayout(PlatformManager.currentMode === 'pc' ? 'desktop' : 'mobile');
    }

    onResponsiveChange(data: { format: string; orientation?: string; breakpoint?: string }) {
        if (PlatformManager?.isManualOverride) return;
        this.applyLayout(data.format);
    }

    applyLayout(format: string) {
        if (!LayoutStrategies) return;

        const StrategyClass =
            format === 'desktop' ? LayoutStrategies.Desktop : LayoutStrategies.Mobile;

        if (!this.currentStrategy || !(this.currentStrategy instanceof StrategyClass)) {
            this.currentStrategy?.exit();
            this.currentStrategy = new StrategyClass(this);
            this.currentStrategy.enter();
        }
    }

    // === Quest Panel ===
    updateQuest(quest: IQuest | null, animate = false) {
        if (!quest) {
            this.hideQuestPanel();
            return;
        }

        const panel = document.getElementById('ui-quest-panel');
        if (panel) panel.style.display = 'block';

        const desc = document.getElementById('quest-description');
        const progress = document.getElementById('quest-progress');
        const bar = document.getElementById('quest-progress-bar');

        if (desc) desc.textContent = quest.description;
        if (progress) progress.textContent = `(${quest.current}/${quest.target})`;

        if (bar) {
            const pct = Math.min(100, (quest.current / quest.target) * 100);
            bar.style.width = `${pct}%`;

            if (animate && VFXController?.triggerUIProgressSparks) {
                // Get position from bar element for VFX
                const rect = bar.getBoundingClientRect();
                VFXController.triggerUIProgressSparks(rect.right, rect.top + rect.height / 2, {
                    color: '#3498DB'
                });
            }
        }
    }

    hideQuestPanel() {
        const panel = document.getElementById('ui-quest-panel');
        if (panel) panel.style.display = 'none';
    }

    // === Screen Fade ===
    fadeInOut(onMidpoint: (() => void) | null) {
        const overlay = document.getElementById('fade-overlay');
        if (!overlay) {
            if (onMidpoint) onMidpoint();
            return;
        }

        if (overlay.classList.contains('fade-active')) return;

        overlay.classList.add('fade-active');
        setTimeout(() => {
            if (onMidpoint) onMidpoint();
            overlay.classList.remove('fade-active');
        }, 1000);
    }

    // === Panel Registry ===
    registerPanel(panel: IUIPanel) {
        if (!this.panels.has(panel.id)) {
            this.panels.set(panel.id, panel);
            Logger.debug('[UIManager]', `Registered panel: ${panel.id}`);

            if (this.currentStrategy) {
                const mode =
                    this.currentStrategy.constructor.name === 'DesktopLayout'
                        ? 'desktop'
                        : 'mobile';
                panel.applyLayout(mode);
            }
        }
    }

    getPanel(id: string): IUIPanel | undefined {
        return this.panels.get(id);
    }

    handleAccordion(openingPanel: IUIPanel) {
        this.panels.forEach((panel) => {
            if (
                panel !== openingPanel &&
                panel.isDocked &&
                panel.isOpen &&
                panel.config.defaultDock === openingPanel.config.defaultDock
            ) {
                panel.close();
            }
        });
    }

    // === Fullscreen UI Management ===
    /**
     * Register a fullscreen UI (call in the UI's constructor)
     * @param {Object} ui - UI object with isOpen and close() method
     */
    registerFullscreenUI(ui: IFullscreenUI) {
        this.fullscreenUIs.add(ui);
    }

    /**
     * Close all other fullscreen UIs except the one being opened
     * @param {Object} exceptUI - The UI that should remain open
     */
    closeOtherFullscreenUIs(exceptUI: IFullscreenUI) {
        this.fullscreenUIs.forEach((ui) => {
            if (ui !== exceptUI && ui.isOpen && typeof ui.close === 'function') {
                ui.close();
            }
        });
        // Also close registered panels
        this.panels.forEach((panel) => {
            if (panel.isOpen) {
                panel.close();
            }
        });
    }

    // === Update Loop ===
    update(_dt: number) {
        // UI mostly event-driven
    }

    toggleDebugCapture() {
        return UICapture?.toggleMode();
    }
    async autoCapture() {
        return UICapture?.autoCapture();
    }
    async captureElement(s: string, f: string) {
        return UICapture?.captureElement(s, f);
    }
    async captureAllZones() {
        return UICapture?.captureAllZones();
    }

    showContextAction(type: string, data: Record<string, unknown>) {
        ContextActionUI?.show(type, data);
    }
    hideContextAction(type: string) {
        ContextActionUI?.hide(type);
    }
    executeContextAction() {
        ContextActionUI?.execute();
    }
}

// Create singleton and export
const UIManager = new UIManagerService();
const debugUICapture = () => UICapture?.captureAllZones();
if (Registry) Registry.register('UIManager', UIManager);

export { UIManagerService, UIManager, debugUICapture };

/**
 * HUDController - Manages the Heads-Up Display
 *
 * Handles Stamina, Health, and Resource counters.
 * Listens to EventBus updates.
 */

import { Logger } from '@core/Logger';
import { EventBus } from '@core/EventBus';
import { GameConstants, getConfig } from '@data/GameConstants';
import { Registry } from '@core/Registry';
import { UIBinder } from '@core/UIBinder';
import { SFX } from '@audio/SFX_Core';



class HUDControllerClass {
    private lastPipCount: number = -1;
    // private readonly RESOLVE_PER_PIP = 5; // Deprecated, use GameConstants

    constructor() {
        this.initListeners();
        Logger.info('[HUDController] Initialized');
    }

    initListeners() {
        if (!EventBus) return;

        EventBus.on(
            GameConstants.Events.HERO_STAMINA_CHANGE,
            (data: { current: number; max: number }) => this.updateStamina(data)
        );
        EventBus.on(
            GameConstants.Events.HERO_HEALTH_CHANGE,
            (data: { current: number; max: number }) => this.updateHealth(data)
        );
        EventBus.on(
            GameConstants.Events.INVENTORY_UPDATED,
            (data: Record<string, number>) =>
                this.updateResources(data)
        );
        EventBus.on(GameConstants.Events.HERO_HOME_STATE_CHANGE, (data: { isHome: boolean }) =>
            this.updateRestButton(data)
        );
    }

    updateStamina(data: { current: number; max: number }) {
        const barContainer = UIBinder.get('ui-resolve-bar');
        if (!barContainer) return;

        // Ensure container exists
        const pipsContainer = UIBinder.ensureContainer(
            'resolve-pips-container',
            'ui-resolve-bar'
        );
        if (!pipsContainer) return;

        // 1. Calculate Target Pip Count (Dynamic Scaling)
        const totalPips = Math.ceil(data.max / GameConstants.UI.RESOLVE_PER_PIP);
        const currentActivePips = Math.ceil(data.current / GameConstants.UI.RESOLVE_PER_PIP);

        // 2. Rebuild DOM if max resolve changed (or init)
        if (pipsContainer.children.length !== totalPips) {
            // Clean up old artifacts if we are rebuilding
            pipsContainer.innerHTML = '';
            for (let i = 0; i < totalPips; i++) {
                UIBinder.create('div', { className: 'resolve-pip', parent: pipsContainer });
            }
            // Reset last count on rebuild to avoid animation spam
            this.lastPipCount = currentActivePips;
        }

        // 3. Update States
        const pips = Array.from(pipsContainer.children) as HTMLElement[];

        pips.forEach((pip, index) => {
            const isActive = index < currentActivePips;

            if (isActive) {
                // Determine if this pip needs to be filled
                if (!pip.classList.contains('active')) {
                    pip.classList.add('active');
                    pip.classList.remove('lost');
                }
            } else {
                // It is empty. Did it JUST lose its active state?
                if (pip.classList.contains('active')) {
                    pip.classList.remove('active');
                    // Only trigger shatter if we aren't initializing (lastPipCount check)
                    // But logic dictates if it WAS active in DOM, it's a valid loss frame.
                    pip.classList.add('lost');

                    // Play Sound
                    SFX?.play('sfx_ui_resolve_shatter');

                    // Clean up animation class after it finishes to reset state strictly
                    setTimeout(() => {
                        pip.classList.remove('lost');
                    }, GameConstants.UI.ANIMATION_SHATTER_MS);
                }
            }
        });

        this.lastPipCount = currentActivePips;
    }

    updateHealth(data: { current: number; max: number }) {
        const bar = UIBinder.get('health-bar');
        const text = UIBinder.get('health-text');
        if (bar) bar.style.width = `${(data.current / data.max) * 100}%`;
        if (text) text.textContent = String(Math.floor(data.current));
    }

    updateRestButton(data: { isHome: boolean }) {
        const btn = document.getElementById('btn-rest');
        if (btn) btn.style.display = data.isHome ? 'flex' : 'none';
    }

    updateResources(inventory: Record<string, number>) {
        if (!inventory) return;

        const map: Record<string, string> = {
            scraps_t1_01: 'res-scrap',
            minerals_t1_01: 'res-iron',
            minerals_t2_01: 'res-fuel',
            gold: 'res-gold'
        };

        for (const [key, elementId] of Object.entries(map)) {
            const el = UIBinder.get(elementId);
            if (el) {
                // Gold might be separate or in items, assuming flat structure from Hero.inventory
                const amount = inventory[key] || 0;
                el.textContent = String(amount);
            }
        }
    }
}

// Register
const HUDController = new HUDControllerClass();
if (Registry) Registry.register('HUDController', HUDController);

// ES6 Module Export
export { HUDControllerClass, HUDController };

/**
 * UIPanel - Base Class for all UI Modules
 *
 * Provides standard functionality for:
 * - Visibility (Open/Close)
 * - Docking (Mobile Modal vs Desktop Sidebar)
 * - Layout State Management
 */

import { UIManager } from '../../ui/UIManager';
import { Logger } from '@core/Logger';
import { Registry } from '@core/Registry';
import { EventBus } from '@core/EventBus';
import { GameConstants } from '@data/GameConstants';
import { UIPanelConfig, UIPanelOptions } from '../../types/ui';

class UIPanel {
    // Property declarations
    id: string;
    config: UIPanelOptions;
    el: HTMLElement | null;
    isOpen: boolean;
    isDocked: boolean;

    /**
     * @param {string} id - DOM Element ID
     * @param {UIPanelOptions} config - Configuration options
     */
    constructor(id: string, config: Partial<UIPanelOptions> = {}) {
        this.id = id;
        this.config = Object.assign(
            {
                id: id,
                visible: false,
                dockable: true,
                defaultDock: 'ui-hud-right',
                modalClass: 'modal-panel',
                dockedClass: 'docked-panel',
                isOpen: false
            } as UIPanelOptions,
            config
        );

        this.el = document.getElementById(this.id);
        this.isOpen = false;
        this.isDocked = false;

        // Register with Manager if it exists
        if (UIManager && UIManager.registerPanel) {
            UIManager.registerPanel(this);
        } else {
            // Fallback retry if UIManager loads later
            setTimeout(() => {
                if (UIManager && UIManager.registerPanel) UIManager.registerPanel(this);
            }, GameConstants.Timing.UI_PANEL_DEBOUNCE_MS);
        }
    }

    /**
     * Initialize logic (override in subclass)
     */
    init() {
        Logger.info(`[UIPanel] Init ${this.id}`);
    }

    /**
     * Toggle visibility
     */
    toggle() {
        if (this.isOpen) this.close();
        else this.open();
    }

    /**
     * Open the panel
     */
    open() {
        if (!this.el) this.el = document.getElementById(this.id);
        if (!this.el) return;

        if (EventBus && GameConstants?.Events) {
            EventBus.emit(GameConstants.Events.UI_FULLSCREEN_OPENED, { source: this });
        }

        // If docked, we might need to close other docked panels (Accordion)
        if (this.isDocked && UIManager) {
            UIManager.handleAccordion(this);
        }

        this.el.style.display = 'flex';
        this.isOpen = true;
        this.onOpen();
    }

    /**
     * Close the panel
     */
    close() {
        if (!this.el) this.el = document.getElementById(this.id);
        if (!this.el) return;

        this.el.style.display = 'none';
        this.isOpen = false;
        this.onClose();
    }

    /**
     * Lifecycle hooks
     */
    onOpen() { }
    onClose() { }

    /**
     * Apply a layout mode (called by LayoutStrategies)
     * @param {string} mode 'mobile' | 'desktop'
     */
    applyLayout(mode: string) {
        if (!this.config.dockable) return;

        if (mode === 'desktop') {
            this.dock();
        } else {
            this.undock();
        }
    }

    /**
     * Dock to sidebar (PC Mode)
     */
    dock() {
        if (this.isDocked) return;

        const target = document.getElementById(this.config.defaultDock);
        if (!target) return;

        if (!this.el) this.el = document.getElementById(this.id);
        if (this.el && this.el.parentElement !== target) {
            target.appendChild(this.el);
            this.el.classList.remove(this.config.modalClass);
            this.el.classList.add(this.config.dockedClass);

            // Hide by default when switching to dock to prevent visual clutter
            // unless it was already maintaining state? Better to close to be safe.
            this.close();

            this.isDocked = true;
        }
    }

    /**
     * Undock to modal layer (Mobile Mode)
     */
    undock() {
        if (!this.isDocked) return;

        const target = document.getElementById('modal-layer');
        if (!target) {
            Logger.error('[UIPanel] Undock Failed: modal-layer not found!');
            return;
        }

        if (!this.el) this.el = document.getElementById(this.id);

        Logger.info(`[UIPanel] Undocking ${this.id} to modal-layer...`);

        if (this.el && this.el.parentElement !== target) {
            target.appendChild(this.el);
            this.el.classList.remove(this.config.dockedClass);
            this.el.classList.add(this.config.modalClass);

            this.close(); // Reset state
            this.isDocked = false;
            Logger.info(`[UIPanel] ${this.id} Undocked Successfully.`);
        }
    }
}

// ES6 Module Export
export { UIPanel };

/**
 * EntityScaling - Standardized logic for entity dimension calculations
 *
 * Ensures consistent application of width, height, and scale from config/registry.
 * Used by Entity constructors and refreshConfig() methods.
 */
import { Logger } from '@core/Logger';
import { Entity } from '../core/Entity';
import type { EntityConfig } from '../types/core';

export const EntityScaling = {
    /**
     * Calculate final dimensions for an entity based on config and defaults.
     *
     * Priority:
     * 1. Instance Config (passed to constructor)
     * 2. Registry Config (from EntityLoader)
     * 3. Defaults
     *
     * Logic:
     * - Base Width/Height = instance.width || registry.width || default
     * - Scale = instance.sizeScale || instance.scale || registry.sizeScale || registry.scale || 1.0
     * - Final = Base * Scale
     *
     * @param {object} instanceConfig - The specific config for this entity instance
     * @param {object} registryConfig - The registry entry for this entity type
     * @param {object} defaults - Fallback values { width, height }
     * @returns {{ width: number, height: number, scale: number }}
     */
    calculateSize(instanceConfig: EntityConfig = {}, registryConfig: EntityConfig = {}, defaults: { width: number, height: number } = { width: 80, height: 80 }) {
        // Priority: Registry (Dashboard) > Instance (Save Data/Map) > Defaults
        // The User explicitly requested that Asset Dashboard settings must be the source of truth,
        // ignoring potential "baked" values in save files or map data.

        // 1. Determine Base Dimensions
        // Registry Config wins to ensure updates propagate to all instances
        const baseWidth = registryConfig.width || instanceConfig.width || defaults.width;
        const baseHeight = registryConfig.height || instanceConfig.height || defaults.height;

        // 2. Determine Scale
        // Registry Config wins.
        // We look for 'sizeScale' first, then 'scale'.
        // We look for 'sizeScale' first, then 'scale'
        // Type assertion needed because these might come from [key: string]: unknown
        const registryScale = (registryConfig.sizeScale ?? registryConfig.scale) as number | undefined;
        const instanceScale = (instanceConfig.sizeScale ?? instanceConfig.scale) as number | undefined;

        // Strict Priority: Registry > Instance > Default (1.0)
        // If Registry has a value, USE IT. Only fallback to instance if Registry is undefined.
        // This ensures "scale 4" in dashboard applies even if map data says "scale 1".
        // Strict Priority: Registry > Instance > Default (1.0)
        // If Registry has a value, USE IT. Only fallback to instance if Registry is undefined.
        // This ensures "scale 4" in dashboard applies even if map data says "scale 1".
        const scale = (registryScale !== undefined) ? registryScale : (instanceScale || 1.0);

        // 3. Calculate Final Dimensions
        // We round to avoid sub-pixel blurring which looks bad in pixel art
        const finalWidth = Math.round(baseWidth * scale);
        const finalHeight = Math.round(baseHeight * scale);

        let source = 'default';
        if (registryScale !== undefined) source = 'registry';
        else if (instanceScale !== undefined) source = 'instance';

        return {
            width: finalWidth,
            height: finalHeight,
            scale: scale,
            baseWidth,
            baseHeight,
            source
        };
    },

    /**
     * Apply calculated dimensions to an entity instance
     * @param {Entity} entity - The entity to update
     * @param {object} config - Configuration used for logging context
     */
    applyToEntity(entity: Entity, instanceConfig: EntityConfig = {}, registryConfig: EntityConfig = {}, defaults: { width: number, height: number }) {
        const size = this.calculateSize(instanceConfig, registryConfig, defaults);

        entity.width = size.width;
        entity.height = size.height;
        entity.scale = size.scale; // Store scale for reference

        // Update collision bounds if they exist
        if (entity.collision && entity.collision.bounds) {
            // Check if bounds were manually overridden in config (don't overwrite custom bounds)
            // If bounds match previous dimensions (or are proportional), update them?
            // Safer strategy: Update bounds to match new width/height unless explicitly set to something else?
            // For now, simpler: Update bounds width/height to match entity width/height
            // (most entities have bounds matching their size)

            // OPTIONAL: Apply bounds scaling factor if defined (some entities have 0.8 collision box)
            // But usually collision.bounds.width IS the final width.
            entity.collision.bounds.width = size.width;
            entity.collision.bounds.height = size.height;
        }

        // Logger.info(`[EntityScaling] Applied size to ${entity.id || 'entity'}: ${size.width}x${size.height} (Scale: ${size.scale})`);
        return size;
    }
};

/**
 * HealthBarRenderer - Utility for rendering health and respawn bars
 *
 * Consolidates duplicated health bar rendering code from Resource.js and Dinosaur.js.
 * Falls back to simple bars when ProgressBarRenderer is unavailable.
 *
 * Owner: VFX Specialist
 */

import { ProgressBarRenderer, ProgressBarOptions } from './ProgressBarRenderer';

// Ambient declaration for global dependency

export interface HealthBarOptions {
    x: number;
    y: number;
    percent: number;
    mode?: 'health' | 'respawn';
    width?: number;
    height?: number;
    entityId?: string;
    animated?: boolean;
}

interface EntityWithPosition {
    x: number;
    y: number;
    height: number;
    id?: string;
}

export interface EntityWithHealth extends EntityWithPosition {
    health: number;
    maxHealth: number;
    components?: {
        health?: {
            health: number;
            maxHealth: number;
        };
    };
}

export interface EntityWithRespawn extends EntityWithPosition {
    respawnTimer: number;
    maxRespawnTime: number;
    currentRespawnDuration?: number;
}

const HealthBarRenderer = {
    /**
     * Draw a health or respawn bar above an entity
     * @param {CanvasRenderingContext2D} ctx
     * @param {HealthBarOptions} options
     * @param {number} options.x - Center X position of entity
     * @param {number} options.y - Top Y position (above entity)
     * @param {number} options.percent - Fill percentage (0-1)
     * @param {string} options.mode - 'health' or 'respawn'
     * @param {number} [options.width=100] - Bar width
     * @param {number} [options.height=14] - Bar height
     * @param {string} [options.entityId] - Optional entity ID for tracking
     * @param {boolean} [options.animated=true] - Whether to animate
     */
    draw(ctx: CanvasRenderingContext2D, options: HealthBarOptions) {
        const {
            x,
            y,
            percent,
            mode = 'health',
            width = 100,
            height = 14,
            entityId,
            animated = true
        } = options;

        const barX = x - width / 2;
        const barY = y;

        // Use ProgressBarRenderer if available
        if (ProgressBarRenderer) {
            ProgressBarRenderer.draw(ctx, {
                x: barX,
                y: barY,
                width,
                height,
                percent,
                mode,
                entityId,
                animated
            });
            return;
        }

        // Fallback: simple colored bar
        ctx.fillStyle = 'rgba(0, 0, 0, 0.6)';
        ctx.fillRect(barX, barY, width, height);

        if (mode === 'health') {
            // Color based on health level
            if (percent > 0.5) {
                ctx.fillStyle = '#4CAF50'; // Green
            } else if (percent > 0.25) {
                ctx.fillStyle = '#F39C12'; // Orange
            } else {
                ctx.fillStyle = '#E74C3C'; // Red
            }
        } else if (mode === 'respawn') {
            ctx.fillStyle = '#3498DB'; // Blue
        } else {
            ctx.fillStyle = '#4CAF50'; // Default green
        }

        ctx.fillRect(barX, barY, width * Math.max(0, Math.min(1, percent)), height);
    },

    /**
     * Draw a health bar for an entity with standard positioning
     * @param {CanvasRenderingContext2D} ctx
     * @param {EntityWithHealth} entity - Entity with x, y: number, width: number, height: number, health, maxHealth
     * @param {Partial<HealthBarOptions>} [options] - Override options
     */
    drawForEntity(ctx: CanvasRenderingContext2D, entity: EntityWithHealth, options: Partial<HealthBarOptions> = {}) {
        const barY = entity.y - entity.height / 2 - 18;

        // Check for HealthComponent first
        const healthComp = entity.components?.health;
        const current = healthComp ? healthComp.health : entity.health;
        const max = healthComp ? healthComp.maxHealth || entity.maxHealth : entity.maxHealth;

        this.draw(ctx, {
            x: entity.x,
            y: barY,
            percent: current / max,
            mode: 'health',
            entityId: entity.id,
            ...options
        });
    },

    /**
     * Draw a respawn bar for an entity with standard positioning
     * @param {CanvasRenderingContext2D} ctx
     * @param {EntityWithRespawn} entity - Entity with x, y: number, respawnTimer, maxRespawnTime
     * @param {Partial<HealthBarOptions>} [options] - Override options
     */
    drawRespawnForEntity(ctx: CanvasRenderingContext2D, entity: EntityWithRespawn, options: Partial<HealthBarOptions> = {}) {
        const barY = entity.y - entity.height / 2 - 18;
        const totalDuration = entity.currentRespawnDuration || entity.maxRespawnTime;
        const percent = Math.max(0, 1 - entity.respawnTimer / totalDuration);

        this.draw(ctx, {
            x: entity.x,
            y: barY,
            percent,
            mode: 'respawn',
            ...options
        });
    }
};

