/**
 * ProjectileVFX - Spawns traveling projectile visual effects
 *
 * Creates projectiles that travel from gun tip to target.
 * Configs: ProjectileVFXConfig. Muzzle: ProjectileMuzzleFlash. Weapon type: ProjectileWeaponType.
 */

import { RenderConfig } from '@config/RenderConfig';
import { LightingSystem } from './LightingSystem';
import { Registry } from '@core/Registry';
import { MathUtils } from '@core/MathUtils';
import { PROJECTILE_CONFIGS, type ProjectileConfig } from './ProjectileVFXConfig';
import { spawnMuzzleFlash } from './ProjectileMuzzleFlash';
import { getWeaponType } from './ProjectileWeaponType';

interface Projectile {
    x: number;
    y: number;
    targetX: number;
    targetY: number;
    angle: number;
    speed: number;
    color: string;
    coreColor: string;
    size: number;
    length: number;
    glow: boolean;
    glowSize: number;
    trail: boolean;
    fade: boolean;
    distance: number;
    traveled: number;
    active: boolean;
    alpha: number;
}

const ProjectileVFX = {
    projectiles: [] as Projectile[],

    /**
     * Spawn projectile(s) from origin to target
     * @param {Object} origin - {x, y} world coordinates of origin (hero)
     * @param {Object} target - {x, y} world coordinates of target
     * @param {string} weaponType - Weapon subtype (pistol, rifle, etc)
     */
    spawn(origin: { x: number; y: number }, target: { x: number; y: number }, weaponType = 'pistol') {
        const config = PROJECTILE_CONFIGS[weaponType] || PROJECTILE_CONFIGS.default;

        // Calculate trajectory
        const dx = target.x - origin.x;
        const dy = target.y - origin.y;
        const angle = Math.atan2(dy, dx);

        // Muzzle offset - distance from hero center to gun muzzle (top-right of weapon image)
        // After sprite rotation, the muzzle is aligned with the aim direction (baseAngle)
        let muzzleOffset = 80; // Default fallback

        if (RenderConfig?.Hero?.WEAPON?.RIFLE) {
            const cfg = RenderConfig.Hero.WEAPON.RIFLE;
            // Weapon image has grip at bottom-left, muzzle at top-right (diagonal)
            // After SPRITE_ROTATION, muzzle points along the aim direction
            // So muzzle position is simply along the aim angle
            muzzleOffset = cfg.TARGET_WIDTH * 0.85; // Muzzle at 85% of weapon length
        }

        // Muzzle is along the aim direction (after rotation aligns it)
        const muzzleX = origin.x + Math.cos(angle) * muzzleOffset;
        const muzzleY = origin.y + Math.sin(angle) * muzzleOffset;

        // Calculate actual travel distance from muzzle to target
        const distance = MathUtils.distance(muzzleX, muzzleY, target.x, target.y);

        spawnMuzzleFlash(muzzleX, muzzleY, angle, config);

        // Spawn projectile(s)
        if (config.pellets) {
            // Shotgun: multiple pellets with spread
            for (let i = 0; i < config.pellets; i++) {
                const spreadAngle = angle + (Math.random() - 0.5) * (config.spread || 0.35);
                this.createProjectile(
                    muzzleX,
                    muzzleY,
                    spreadAngle,
                    distance,
                    config,
                    target
                );
            }
        } else {
            // Single projectile
            this.createProjectile(muzzleX, muzzleY, angle, distance, config, target);
        }
    },

    /**
     * Create a single projectile
     */
    createProjectile(x: number, y: number, angle: number, distance: number, config: ProjectileConfig, target: { x: number; y: number }) {
        const projectile: Projectile = {
            x: x,
            y: y,
            targetX: target.x,
            targetY: target.y,
            angle: angle,
            speed: config.speed,
            color: config.color,
            coreColor: config.coreColor || '#FFFFFF',
            size: config.size,
            length: config.length,
            glow: config.glow || false,
            glowSize: config.glowSize || config.size * 2,
            trail: config.trail || false,
            fade: config.fade || false,
            distance: distance,
            traveled: 0,
            active: true,
            alpha: 1.0
        };

        this.projectiles.push(projectile);
    },

    /**
     * Update all active projectiles
     * @param {number} dt - Delta time in milliseconds
     */
    update(dt: number) {
        // Convert dt from milliseconds to seconds
        const dtSeconds = dt / 1000;

        for (let i = this.projectiles.length - 1; i >= 0; i--) {
            const p = this.projectiles[i];

            // Move projectile
            const moveDistance = p.speed * dtSeconds;
            p.x += Math.cos(p.angle) * moveDistance;
            p.y += Math.sin(p.angle) * moveDistance;
            p.traveled += moveDistance;

            // Check if reached target
            if (p.traveled >= p.distance) {
                p.active = false;
            }

            // Submit light to LightingSystem (if active and glowing)
            if (
                p.active &&
                p.glow &&
                LightingSystem &&
                typeof LightingSystem.addLight === 'function'
            ) {
                try {
                    const radius = 50 + (p.glowSize || p.size * 2) * 2;
                    // Elongation based on projectile length/size ratio
                    const elongation = 1.5; // Stretch 1.5x along direction
                    LightingSystem.addLight(
                        p.x,
                        p.y,
                        radius,
                        p.color,
                        p.alpha,
                        p.angle,
                        elongation
                    );
                } catch (e) {
                    // Silently ignore lighting errors
                }
            }

            // Fade effect
            if (p.fade) {
                p.alpha = 1.0 - p.traveled / p.distance;
            }

            // Remove inactive projectiles
            if (!p.active) {
                this.projectiles.splice(i, 1);
            }
        }
    },

    /**
     * Render all active projectiles
     * @param {CanvasRenderingContext2D} ctx - Canvas context (already translated by viewport)
     */
    render(ctx: CanvasRenderingContext2D) {
        if (!ctx) return;

        for (const p of this.projectiles) {
            if (!p.active) continue;

            const screenX = p.x;
            const screenY = p.y;

            // Calculate tail position
            const tailX = screenX - Math.cos(p.angle) * p.length;
            const tailY = screenY - Math.sin(p.angle) * p.length;

            ctx.save();
            ctx.globalAlpha = p.alpha;

            // Layer 1: Outer glow (if enabled)
            if (p.glow) {
                ctx.shadowColor = p.color;
                ctx.shadowBlur = p.glowSize;
                ctx.strokeStyle = p.color;
                ctx.lineWidth = p.size * 1.5;
                ctx.lineCap = 'round';
                ctx.globalAlpha = p.alpha * 0.5;
                ctx.beginPath();
                ctx.moveTo(tailX, tailY);
                ctx.lineTo(screenX, screenY);
                ctx.stroke();
                ctx.shadowBlur = 0;
            }

            // Layer 2: Main tracer body
            ctx.globalAlpha = p.alpha;
            ctx.strokeStyle = p.color;
            ctx.lineWidth = p.size;
            ctx.lineCap = 'round';
            ctx.beginPath();
            ctx.moveTo(tailX, tailY);
            ctx.lineTo(screenX, screenY);
            ctx.stroke();

            // Layer 3: Bright white-hot core (head of projectile)
            const coreLength = p.length * 0.4;
            const coreTailX = screenX - Math.cos(p.angle) * coreLength;
            const coreTailY = screenY - Math.sin(p.angle) * coreLength;
            ctx.strokeStyle = p.coreColor;
            ctx.lineWidth = p.size * 0.6;
            ctx.beginPath();
            ctx.moveTo(coreTailX, coreTailY);
            ctx.lineTo(screenX, screenY);
            ctx.stroke();

            if (p.trail) {
                ctx.globalAlpha = p.alpha * 0.2;
                ctx.strokeStyle = p.color;
                ctx.lineWidth = p.size * 0.4;
                const trailLength = p.length * 2.5;
                const trailTailX = screenX - Math.cos(p.angle) * trailLength;
                const trailTailY = screenY - Math.sin(p.angle) * trailLength;
                ctx.beginPath();
                ctx.moveTo(trailTailX, trailTailY);
                ctx.lineTo(tailX, tailY);
                ctx.stroke();
            }

            ctx.restore();
        }
    },

    getWeaponType: getWeaponType
};

// Export
if (Registry) Registry.register('ProjectileVFX', ProjectileVFX);

export { ProjectileVFX };

/**
 * VFX Controller (System 2.0)
 * Orchestrates all visual effects using a Data-Driven Sequencer.
 *
 * Owner: VFX Specialist
 */

import { Logger } from '@core/Logger';
import { EventBus } from '@core/EventBus';
import { GameConstants } from '@data/GameConstants';
import { ParticleSystem } from './ParticleSystem';
import { Registry } from '@core/Registry';
import { ProjectileVFX } from './ProjectileVFX';
import { MeleeTrailVFX } from './MeleeTrailVFX';
import { FloatingTextManager, FloatingText } from './FloatingText';
import { VFXConfig } from '@data/VFXConfig';
import { GameRenderer } from '@core/GameRenderer';
import type { IGame } from '../types/core.d';
import type { ParticleOptions, VFXSequence, VFXCue } from '../types/vfx';

class VFXSystem {
    // Property declarations
    game: IGame | null = null;
    bgParticles: typeof ParticleSystem | null = null;
    fgParticles: typeof ParticleSystem | null = null;
    texts: FloatingText[] = [];
    activeSequences: Array<{
        name?: string;
        x: number;
        y: number;
        elapsed: number;
        cues: Array<{ time: number; type: string; options?: ParticleOptions }>;
        options?: ParticleOptions;
    }> = [];
    initialized: boolean = false;

    constructor() {
        Logger.info('[VFXSystem] Constructed');
    }

    init(game: IGame) {
        this.game = game;
        // Initialize dual-layer canvas system
        // Note: ParticleSystem is expected to be a global or imported class
        if (ParticleSystem) {
            this.bgParticles = Object.create(ParticleSystem);
            this.fgParticles = Object.create(ParticleSystem);

            this.bgParticles.init('vfx-canvas');
            this.fgParticles.init('vfx-canvas-fg');
            Logger.info('[VFXSystem] Particles initialized');
        } else {
            Logger.error('[VFXSystem] ParticleSystem not found! Check load order.');
        }

        if (EventBus && GameConstants) {
            EventBus.on(GameConstants.Events.VFX_PLAY_FOREGROUND, (data: { x: number; y: number; options?: ParticleOptions }) => {
                if (data && typeof data.x === 'number' && typeof data.y === 'number') {
                    this.playForeground(data.x, data.y, data.options);
                }
            });
            EventBus.on(GameConstants.Events.HERO_LEVEL_UP, (data: { hero?: { x: number; y: number } }) => {
                const hero = data?.hero;
                if (hero && typeof hero.x === 'number' && typeof hero.y === 'number') {
                    const opts = VFXConfig.TEMPLATES?.LEVEL_UP_FX || { type: 'burst', color: '#FFD700', count: 30, lifetime: 1000 };
                    this.playForeground(hero.x, hero.y, opts);
                }
            });
        }

        this.initialized = true;
        Logger.info('[VFXSystem] Initialized');
    }

    /**
     * Update all VFX systems
     */
    update(dt: number) {
        if (!this.initialized) return;

        if (this.bgParticles) this.bgParticles.update(dt);
        if (this.fgParticles) this.fgParticles.update(dt);

        // Update Texts
        for (let i = this.texts.length - 1; i >= 0; i--) {
            const text = this.texts[i];
            text.update(dt);
            if (!text.active) {
                this.texts.splice(i, 1);
            }
        }

        // Update Active Sequences
        for (let i = this.activeSequences.length - 1; i >= 0; i--) {
            const seq = this.activeSequences[i];
            seq.elapsed += dt;

            // Check for cues to fire
            while (seq.cues.length > 0 && seq.cues[0].time <= seq.elapsed) {
                const cue = seq.cues.shift(); // Remove and fire
                this.executeCue(cue, seq.x, seq.y, seq.options);
            }

            // Remove finished sequences
            if (seq.cues.length === 0) {
                this.activeSequences.splice(i, 1);
            }
        }

        // Update traveling projectiles
        if (ProjectileVFX) {
            ProjectileVFX.update(dt);
        }

        // Update melee trails
        if (MeleeTrailVFX) {
            MeleeTrailVFX.update(dt);
        }

        // Update floating text (damage numbers, etc.)
        if (FloatingTextManager) {
            FloatingTextManager.update(dt);
        }
    }

    /**
     * Execute a single cue from a sequence
     */
    executeCue(cue: VFXCue, x: number, y: number, _contextOptions: ParticleOptions = {}) {
        // Resolve Template if present
        let config: ParticleOptions = {};

        if (cue.template && VFXConfig && VFXConfig.TEMPLATES[cue.template]) {
            // Merge Template with Cue Params (Cue wins)
            config = { ...VFXConfig.TEMPLATES[cue.template], ...(cue.params || {}) };
        } else if (cue.type) {
            // Direct definition
            // Cast strictly to ensure 'type' matches allowed string literals
            config = { type: cue.type as ParticleOptions['type'], ...(cue.params || {}) };
        } else {
            // cue.template might refer to a template that doesn't exist?
            Logger.warn('[VFXSystem] Invalid cue config:', cue);
            return;
        }

        // Determine Layer
        const layer = cue.layer || 'fg';
        const system = layer === 'bg' ? this.bgParticles : this.fgParticles;

        if (!system) return;

        // Emit Particle
        system.emit(x, y, config);
    }

    /**
     * Play a named sequence from VFXConfig
     * @param {string} sequenceName - Key in VFXConfig.SEQUENCES
     * @param {number} x - World X
     * @param {number} y - World Y
     * @param {object} options - Optional overrides
     */
    playSequence(sequenceName: string, x: number, y: number, options: ParticleOptions = {}) {
        if (!VFXConfig || !VFXConfig.SEQUENCES[sequenceName]) {
            Logger.warn(`[VFXSystem] Sequence not found: ${sequenceName}`);
            return;
        }

        const rawSequence = VFXConfig.SEQUENCES[sequenceName];

        // GC Optimization: Build cues array more efficiently
        const cues = [];
        for (let i = 0; i < rawSequence.length; i++) {
            const src = rawSequence[i];
            cues.push({
                time: src.time,
                template: src.template,
                type: src.type,
                layer: src.layer,
                params: src.params
            });
        }
        // Sort by time just in case config is out of order
        cues.sort((a, b) => a.time - b.time);

        this.activeSequences.push({
            name: sequenceName,
            cues: cues,
            elapsed: 0,
            x: x,
            y: y,
            options: options
        });
    }

    /**
     * Helper: Play a generic effect immediately (using Template or raw config)
     */
    playEffect(configOrTemplateName: string | ParticleOptions, x: number, y: number, layer = 'fg') {
        let config = configOrTemplateName;

        // Check if string -> Template
        if (typeof configOrTemplateName === 'string') {
            if (VFXConfig && VFXConfig.TEMPLATES[configOrTemplateName]) {
                config = VFXConfig.TEMPLATES[configOrTemplateName];
            } else {
                Logger.warn(`[VFXSystem] Template not found: ${configOrTemplateName}`);
                return;
            }
        }

        const system = layer === 'bg' ? this.bgParticles : this.fgParticles;
        if (system) system.emit(x, y, config);
    }

    /**
     * Render (Called by GameRenderer with main context)
     */
    render(ctx: CanvasRenderingContext2D) {
        if (!this.initialized) return;

        // Render all floating texts to the game canvas
        for (const text of this.texts) {
            text.render(ctx);
        }

        // Render traveling projectiles (no camera offset needed - ctx is already translated)
        if (ProjectileVFX) {
            ProjectileVFX.render(ctx);
        }

        // Render melee trails (needs viewport for world-to-screen conversion)
        if (MeleeTrailVFX && GameRenderer) {
            const viewport = {
                x: GameRenderer.viewport.x,
                y: GameRenderer.viewport.y,
                scale: 1, // GameRenderer doesn't use scale, always 1:1
                screenX: 0,
                screenY: 0
            };
            MeleeTrailVFX.render(ctx, viewport);
        }

        // Render floating text (damage numbers, etc.)
        if (FloatingTextManager) {
            FloatingTextManager.render(ctx);
        }
    }

    /**
     * Render Foreground Particles to their own overlay canvas
     * Called by Game.js main loop, NOT GameRenderer
     */
    renderForeground() {
        if (!this.initialized || !this.fgParticles) return;
        this.fgParticles.render();
    }

    /**
     * Legacy Compat: Play a foreground effect (above UI)
     */
    playForeground(x: number, y: number, options: ParticleOptions = {}) {
        if (this.fgParticles) this.fgParticles.emit(x, y, options);
    }

    /**
     * Legacy Compat: Play a background effect (behind UI)
     */
    playBackground(x: number, y: number, options: ParticleOptions = {}) {
        if (this.bgParticles) this.bgParticles.emit(x, y, options);
    }

    /**
     * Map UI DOM coordinates to Canvas coordinates
     */
    uiToCanvas(clientX: number, clientY: number, canvasId: string = 'vfx-canvas-fg') {
        const canvas = document.getElementById(canvasId) as HTMLCanvasElement;
        if (!canvas) return { x: 0, y: 0 };

        const rect = canvas.getBoundingClientRect();
        const scaleX = canvas.width / rect.width;
        const scaleY = canvas.height / rect.height;

        return {
            x: (clientX - rect.left) * scaleX,
            y: (clientY - rect.top) * scaleY
        };
    }

    /**
     * Spawn floating text at world coordinates
     */
    spawnFloatingText(text: string, worldX: number, worldY: number, color = '#FFD700', duration = 2000) {
        if (FloatingText) {
            // FloatingText expects a config object, not separate color/duration params
            const config = {
                color: color,
                floatDuration: duration / 2000, // Convert ms to seconds
                holdDuration: 0.2
            };
            const ft = new FloatingText(text, worldX, worldY, config, 0);
            this.texts.push(ft);
        }
    }

    createExplosion(x: number, y: number) {
        // Redirect to sequence system
        this.playSequence('EXPLOSION_GENERIC', x, y);
    }

    getActiveCount(): number {
        let count = 0;
        if (this.fgParticles?.particles) count += this.fgParticles.particles.length;
        if (this.bgParticles?.particles) count += this.bgParticles.particles.length;
        count += this.texts.length;
        count += this.activeSequences.length;
        return count;
    }

    triggerUIProgressSparks(x: number, y: number, _config: ParticleOptions = {}) {
        this.playForeground(x, y, {
            type: 'spark',
            color: '#FFD700',
            count: 8,
            speed: 5,
            lifetime: 500,
            size: 3
        });
    }

    get presets() {
        return VFXConfig?.TEMPLATES || {};
    }
}

// Export Singleton
const VFXController = new VFXSystem();

// Register at module load time
Registry.register('VFXController', VFXController);

export { VFXSystem, VFXController };

/**
 * RainVFX - Modular Weather Effect
 * Handles high-velocity falling lines with screen wrapping.
 * Simulates world-space persistence by shifting particles opposite to camera movement.
 */
import { MathUtils } from '@core/MathUtils';

interface RainParticle {
    x: number;
    y: number;
    len: number;
    speed: number;
    alpha: number;
    renderDx?: number;
    renderDy?: number;
}

class RainVFX {
    active: boolean;
    particles: RainParticle[];
    count: number;
    speedBase: number;
    angle: number;
    isStorm: boolean = false;

    constructor() {
        this.active = false;
        this.particles = [];
        this.count = 150; // Max raindrops
        this.speedBase = 15;
        this.angle = 0.2; // Radians lean (right)
    }

    init() {
        // Pool instantiation
        for (let i = 0; i < this.count; i++) {
            this.particles.push({
                x: Math.random() * innerWidth,
                y: Math.random() * innerHeight,
                len: 10 + Math.random() * 15,
                speed: this.speedBase + Math.random() * 5,
                alpha: 0.3 + Math.random() * 0.3
            });
        }
    }

    update(dt: number, delta: { x: number; y: number } | null, viewport: { width: number; height: number } | null, wind: { currentX: number } | null, isStorm: boolean) {
        if (!this.active) return;

        this.isStorm = isStorm;

        // Visual Scale
        const lenScale = isStorm ? 2.5 : 1.0; // Increased to 2.5x for impact

        // Assume 60fps baseline for simple physics
        const timeScale = dt / 16.666;

        // Use viewport dimensions if available, else fallback
        const width = viewport ? viewport.width : innerWidth;
        const height = viewport ? viewport.height : innerHeight;

        // If viewport is missing, we can't do world space shift, default to 0
        const dx = delta ? delta.x : 0;
        const dy = delta ? delta.y : 0;

        // Wind Velocity (pixels/sec)
        const windSpeed = wind ? wind.currentX : 0;

        // Update Angle based on wind?
        // Base angle 0.2 rad (~11 deg).
        // 100 wind -> +0.5 rad?
        if (wind) {
            this.angle = 0.2 + wind.currentX / 300; // 300 force = +1 rad (~57 deg)
        }

        for (const p of this.particles) {
            // 1. Apply Camera Move (Shift opposite to camera to stay in world)
            p.x -= dx;
            p.y -= dy;

            // 2. Move (Falling)
            // Calculate Velocity Components (per second approx for direction)
            const vX_sec = p.speed * 0.2 + windSpeed;
            const vY_sec = p.speed;

            // Frame Delta
            const vx_frame = vX_sec * (dt / 1000); // or use timeScale logic: p.speed * timeScale is pixels/frame
            const vy_frame = vY_sec * (dt / 1000); //

            // Consistent Physics Update (using timeScale logic for consistency with original tuning)
            // Original: p.y += p.speed * timeScale;
            // timeScale = dt / 16.666
            // So p.speed is pixels per ~16ms? No, p.speed is pixels/frame at 60fps.
            // Let's stick to modifying p.x/p.y as before, but calculating angle from that.

            const moveY = p.speed * timeScale;
            const moveX = p.speed * 0.2 * timeScale + windSpeed * (dt / 1000);

            p.x += moveX;
            p.y += moveY;

            // 3. Update Visual Vector (Align streak with velocity)
            // Normalize velocity vector
            const mag = MathUtils.distance(0, 0, moveX, moveY);
            // Effective visual length based on storm state
            const effectiveLen = p.len * lenScale;

            if (mag > 0.001) {
                // Point tail backwards
                p.renderDx = (moveX / mag) * effectiveLen;
                p.renderDy = (moveY / mag) * effectiveLen;
            } else {
                p.renderDx = 0;
                p.renderDy = effectiveLen;
            }

            // 4. Wrapping
            // If particle goes off-screen, wrap it to the other side
            // Ideally we want to wrap around the *viewport bounds*
            // Since we are rendering in "Screen Space" relative to camera (0,0 is top-left of screen),
            // The particles are effectively in screen coords.
            // Wait, if p.x -= dx, p.x is effectively WorldX - CameraX.
            // So p.x IS screen coordinate.
            // So we just wrap 0..width.

            if (p.y > height) {
                p.y = -p.len;
                p.x = Math.random() * width;
            }
            if (p.y < -p.len - 100) {
                // If camera moves UP fast (dy negative -> p.y increases. If dy positive (cam down) -> p.y decreases)
                // If p.y becomes too negative (cam moved down past it)
                p.y = height + p.len;
                p.x = Math.random() * width;
            }

            if (p.x > width) {
                p.x = -10;
            } else if (p.x < -10) {
                p.x = width;
            }
        }
    }

    render(ctx: CanvasRenderingContext2D) {
        if (!this.active) return;

        ctx.strokeStyle = 'rgba(174, 194, 224, 0.6)'; // Blue-ish white
        ctx.lineWidth = this.isStorm ? 2 : 1;
        ctx.beginPath();

        for (const p of this.particles) {
            ctx.moveTo(p.x, p.y);
            // Draw Trail (Backwards from head)
            // p.x, p.y is the head (bottom)
            ctx.lineTo(p.x - (p.renderDx || 0), p.y - (p.renderDy || p.len));
        }

        ctx.stroke();
    }
}

