                    }
                }
            }
        }

        const wasAtHome = this._heroAtHome || false;
        hero.isAtHomeOutpost = isAtHome;

        if (isAtHome && !wasAtHome) {
            if (EventBus) EventBus.emit(GameConstants.Events.HOME_BASE_ENTERED);
            Logger.debug('[HomeBase]', 'Hero entered rest area');
        } else if (!isAtHome && wasAtHome) {
            if (EventBus) EventBus.emit(GameConstants.Events.HOME_BASE_EXITED);
            Logger.debug('[HomeBase]', 'Hero exited rest area');
        }
        this._heroAtHome = isAtHome;

        // === Forge Proximity Detection ===
        if (this._forgePos) {
            const dx = hero.x - this._forgePos.x;
            const dy = hero.y - this._forgePos.y;
            const dist = Math.sqrt(dx * dx + dy * dy);

            const wasAtForge = this._heroAtForge || false;
            const isAtForge = dist < getConfig().Interaction.FORGE_AREA_RADIUS;

            if (isAtForge && !wasAtForge) {
                if (EventBus) EventBus.emit(GameConstants.Events.FORGE_ENTERED);
                Logger.debug('[HomeBase]', 'Hero entered forge area');
            } else if (!isAtForge && wasAtForge) {
                if (EventBus) EventBus.emit(GameConstants.Events.FORGE_EXITED);
                Logger.debug('[HomeBase]', 'Hero exited forge area');
            }

            this._heroAtForge = isAtForge;
        }
    },

    // NOTE: Tree spawning moved to SpawnManager.spawnHomeIslandTrees()
    // Trees are now queried via the treeResources getter from EntityManager

    render(ctx: CanvasRenderingContext2D) {
        if (!this._cachedBounds && WorldManager) {
            const spawn = WorldManager.getHeroSpawnPosition();
            if (spawn) {
                const size = 2000;
                this._cachedBounds = {
                    x: spawn.x - size / 2,
                    y: spawn.y - size / 2,
                    width: size,
                    height: size
                };
                if (AssetLoader) {
                    this._outpostPath = AssetLoader.getImagePath('building_residential_01');
                    this._forgePath = AssetLoader.getImagePath('building_industrial_01');
                    this._treePath = AssetLoader.getImagePath('node_woodcutting_t1_01');
                    this._treeConsumedPath = AssetLoader.getImagePath('node_woodcutting_t1_01_consumed');
                }
            }
        }
        const _ = this.treeResources;
        renderHomeBase(ctx, this as unknown as import('./HomeBaseRenderer').HomeBaseRenderState);
    },

    /**
     * Get the safe spawn area (center of home island, away from trees)
     * @returns {{x: number, y: number, width: number, height: number}}
     */
    getSafeArea() {
        if (!this._cachedBounds) return null;

        const b = this._cachedBounds;
        const border = this.treeBorderWidth;
        return {
            x: b.x + border,
            y: b.y + border,
            width: b.width - border * 2,
            height: b.height - border * 2
        };
    },

    /**
     * Check if blocking by trees - now always returns false (trees don't block)
     * @returns {boolean}
     */
    isBlockedByTrees(_x: number, _y: number) {
        return false; // Trees are resources, not barriers
    }
};

if (Registry) Registry.register('HomeBase', HomeBase);

export { HomeBase };

/**
 * MapObjectSpawner - Spawns entities from map editor data
 *
 * Listens to BroadcastChannel 'game-map-updates' for real-time sync.
 * Fetches initial map on init when running with dashboard.
 */
import { Logger } from '@core/Logger';
import { entityManager } from '@core/EntityManager';
import { EntityRegistry } from '@entities/EntityLoader';
import { Prop } from './Prop';
import { Resource } from '../gameplay/Resource';
import { Enemy } from '../gameplay/EnemyCore';
import { Boss } from '../gameplay/Boss';
import { Registry } from '@core/Registry';
import { fetchMapData, getObjectsFromMapData, getPrefetchedMapData, clearPrefetchedMapData } from './MapDataService';
import type { MapData } from './MapDataService';
import type { Mapgen4Param, ManualTownsAndRailroads } from '../tools/map-editor/Mapgen4Generator';
import type { MapObject, HeroSpawnPosition } from '../tools/map-editor/MapEditorTypes';
import type { IEntity } from '../types/core';

const CHANNEL_NAME = 'game-map-updates';
const POS_TOLERANCE = 2;

function makeKey(x: number, y: number): string {
    return `${Math.round(x)},${Math.round(y)}`;
}

function findEntityAt(
    map: Map<string, IEntity>,
    x: number,
    y: number,
    id?: string
): { entity: IEntity; key: string } | null {
    const roundX = Math.round(x);
    const roundY = Math.round(y);
    for (const [key, entity] of map) {
        const [kx, ky] = key.split(',').map(Number);
        if (Math.abs(kx - roundX) <= POS_TOLERANCE && Math.abs(ky - roundY) <= POS_TOLERANCE) {
            if (!id || (entity as { resourceType?: string; registryId?: string }).resourceType === id || (entity as { registryId?: string }).registryId === id) {
                return { entity, key };
            }
        }
    }
    return null;
}

function spawnEntityForMapObject(obj: MapObject): IEntity | null {
    const { id, x, y } = obj;

    if (EntityRegistry.environment?.[id]) {
        const prop = new Prop({ x, y, registryId: id });
        entityManager.add(prop);
        return prop;
    }

    if (EntityRegistry.nodes?.[id] || EntityRegistry.resources?.[id]) {
        const resource = new Resource({ x, y, resourceType: id, isMapPlaced: true });
        entityManager.add(resource);
        return resource;
    }

    if (EntityRegistry.enemies?.[id]) {
        const enemy = new Enemy({ x, y, enemyType: id });
        entityManager.add(enemy);
        return enemy;
    }

    if (EntityRegistry.bosses?.[id]) {
        const boss = new Boss({ x, y, bossType: id });
        entityManager.add(boss);
        return boss;
    }

    Logger.warn(`[MapObjectSpawner] Unknown asset id: ${id}`);
    return null;
}

function clearMapSpawnedEntities(map: Map<string, IEntity>): void {
    for (const entity of map.values()) {
        entityManager.remove(entity);
    }
    map.clear();
}

const MapObjectSpawner = {
    _entityMap: new Map<string, IEntity>(),
    _initialized: false,

    async init(_game?: unknown): Promise<void> {
        if (this._initialized) return;

        this._setupChannel();
        await this._loadInitialMap();
        this._initialized = true;
        Logger.info('[MapObjectSpawner] Initialized');
    },

    _setupChannel(): void {
        if (typeof window === 'undefined' || typeof BroadcastChannel === 'undefined') return;

        const channel = new BroadcastChannel(CHANNEL_NAME);
        channel.onmessage = (event: MessageEvent) => {
            const { type, id, x, y, oldX, oldY, newX, newY, data } = event.data || {};

            switch (type) {
                case 'MAP_FULL':
                    this._applyFullMap(data);
                    break;
                case 'MAP_HERO_SPAWN':
                    if (typeof x === 'number' && typeof y === 'number') {
                        const wm = Registry?.get<{ setHeroSpawn: (x: number, y: number) => void }>('WorldManager');
                        wm?.setHeroSpawn(x, y);
                    }
                    break;
                case 'MAP_OBJECT_ADD':
                    if (id != null && x != null && y != null) {
                        this._addOne({ id, x, y });
                    }
                    break;
                case 'MAP_OBJECT_REMOVE':
                    if (x != null && y != null) {
                        this._removeOne(x, y, id);
                    }
                    break;
                case 'MAP_OBJECT_MOVE':
                    if (id != null && oldX != null && oldY != null && newX != null && newY != null) {
                        this._moveOne(id, oldX, oldY, newX, newY);
                    }
                    break;
            }
        };
        Logger.info('[MapObjectSpawner] Listening for map updates');
    },

    async _loadInitialMap(): Promise<void> {
        const data = getPrefetchedMapData() ?? (await fetchMapData());
        if (data) {
            this._applyFullMap(data);
        }
        clearPrefetchedMapData();
    },

    _applyFullMap(data: MapData | null): void {
        if (data?.mapgen4Param) {
            const worldManager = Registry?.get<{ setMapgen4ParamAndRebuild: (p: Mapgen4Param, m?: ManualTownsAndRailroads) => void }>('WorldManager');
            worldManager?.setMapgen4ParamAndRebuild(data.mapgen4Param, {
                manualTowns: data.manualTowns,
                manualStations: data.manualStations,
                railroadWaypoints: data.railroadWaypoints
            });
        }
        const worldManager = Registry?.get<{ setHeroSpawn: (x: number, y: number) => void; clearHeroSpawn: () => void }>('WorldManager');
        if (data?.heroSpawn && typeof data.heroSpawn.x === 'number' && typeof data.heroSpawn.y === 'number') {
            worldManager?.setHeroSpawn(data.heroSpawn.x, data.heroSpawn.y);
        } else {
            worldManager?.clearHeroSpawn();
        }
        clearMapSpawnedEntities(this._entityMap);
        const objects = getObjectsFromMapData(data);
        for (const obj of objects) {
            const entity = spawnEntityForMapObject(obj);
            if (entity) {
                this._entityMap.set(makeKey(obj.x, obj.y), entity);
            }
        }
        Logger.info(`[MapObjectSpawner] Loaded ${objects.length} objects from map`);
    },

    _addOne(obj: MapObject): void {
        const existing = findEntityAt(this._entityMap, obj.x, obj.y);
        if (existing) return;

        const entity = spawnEntityForMapObject(obj);
        if (entity) {
            this._entityMap.set(makeKey(obj.x, obj.y), entity);
        }
    },

    _removeOne(x: number, y: number, id?: string): void {
        const found = findEntityAt(this._entityMap, x, y, id);
        if (found) {
            entityManager.remove(found.entity);
            this._entityMap.delete(found.key);
        }
    },

    _moveOne(id: string, oldX: number, oldY: number, newX: number, newY: number): void {
        const found = findEntityAt(this._entityMap, oldX, oldY, id);
        if (found) {
            found.entity.x = newX;
            found.entity.y = newY;
            this._entityMap.delete(found.key);
            this._entityMap.set(makeKey(newX, newY), found.entity);
        }
    }
};

if (typeof window !== 'undefined') {
    (window as unknown as { MapObjectSpawner: typeof MapObjectSpawner }).MapObjectSpawner = MapObjectSpawner;
}

export { MapObjectSpawner };

/**
 * WorldManager - Mapgen4 polygon map integration
 *
 * Replaces WorldManager for the main game. Builds mesh+map from mapgen4,
 * provides world size, hero spawn, and stub isWalkable/isBlocked (walk everywhere).
 * Entity placement is done via map editor.
 *
 * Owner: Director
 */

import { Logger } from '@core/Logger';
import { Registry } from '@core/Registry';
import { AssetLoader } from '@core/AssetLoader';
import { getPrefetchedMapData } from './MapDataService';
import { buildMeshAndMap, computeTownsAndRoads, DEFAULT_MAPGEN4_PARAM } from '../tools/map-editor/Mapgen4Generator';
import type {
    MeshAndMap,
    Mapgen4Param,
    TownSite,
    RoadSegment,
    RailroadCrossing,
    ManualTownsAndRailroads
} from '../tools/map-editor/Mapgen4Generator';

/** Deep-merge loaded mapgen4Param with defaults so towns/railroads are never lost. */
function mergeMapgen4Param(loaded: Mapgen4Param | undefined): Mapgen4Param {
    if (!loaded) return DEFAULT_MAPGEN4_PARAM;
    return {
        ...DEFAULT_MAPGEN4_PARAM,
        ...loaded,
        elevation: { ...DEFAULT_MAPGEN4_PARAM.elevation, ...loaded.elevation },
        biomes: { ...DEFAULT_MAPGEN4_PARAM.biomes, ...loaded.biomes },
        rivers: { ...DEFAULT_MAPGEN4_PARAM.rivers, ...loaded.rivers },
        towns: loaded.towns
            ? { ...DEFAULT_MAPGEN4_PARAM.towns!, ...loaded.towns }
            : DEFAULT_MAPGEN4_PARAM.towns,
        roads: loaded.roads
            ? { ...DEFAULT_MAPGEN4_PARAM.roads!, ...loaded.roads }
            : DEFAULT_MAPGEN4_PARAM.roads,
        railroads: loaded.railroads
            ? { ...DEFAULT_MAPGEN4_PARAM.railroads!, ...loaded.railroads }
            : DEFAULT_MAPGEN4_PARAM.railroads
    };
}

const MESH_SIZE = 1000;
const WORLD_SIZE = 160000;
const SCALE = WORLD_SIZE / MESH_SIZE;

class WorldManagerService {
    private meshAndMap: MeshAndMap | null = null;
    private param: Mapgen4Param = DEFAULT_MAPGEN4_PARAM;

    private _heroSpawn: { x: number; y: number } | null = null;
    private _cachedTowns: TownSite[] = [];
    private _cachedRoadSegments: RoadSegment[] = [];
    private _cachedRailroadPath: number[] = [];
    private _cachedRailroadCrossings: RailroadCrossing[] = [];
    private _cachedRailroadStationIds: number[] = [];
    private _manualData?: ManualTownsAndRailroads;

    constructor() {
        Logger.info('[WorldManager] Constructed');
    }

    private buildMeshAndCacheTownsRoads(): void {
        this.meshAndMap = buildMeshAndMap(this.param);
        const { towns, roadSegments, railroadPath, railroadCrossings, railroadStationIds } =
            computeTownsAndRoads(
                this.meshAndMap!.mesh,
                this.meshAndMap!.map,
                this.param,
                this._manualData
            );
        this._cachedTowns = towns;
        this._cachedRoadSegments = roadSegments;
        this._cachedRailroadPath = railroadPath;
        this._cachedRailroadCrossings = railroadCrossings;
        this._cachedRailroadStationIds = railroadStationIds;

        Logger.info(
            '[WorldManager] Generated:',
            towns.length, 'towns,',
            roadSegments.length, 'road segments,',
            railroadPath.length, 'railroad path nodes,',
            railroadCrossings.length, 'crossings.',
            'Railroads enabled:', !!this.param.railroads?.enabled,
            'Towns enabled:', !!this.param.towns?.enabled
        );

        if (railroadPath.length >= 2) {
            for (const biome of ['grasslands', 'tundra', 'desert', 'badlands']) {
                AssetLoader.preloadImage(`ground_base_gravel_${biome}_01`);
                AssetLoader.preloadImage(`arch_railtrack_metal_${biome}_clean`);
                AssetLoader.preloadImage(`arch_railtrack_wood_01_${biome}_clean`);
                AssetLoader.preloadImage(`arch_railtrack_wood_02_${biome}_clean`);
                AssetLoader.preloadImage(`arch_railtrack_wood_03_${biome}_clean`);
            }
        }
    }

    init() {
        Logger.info('[WorldManager] init() called');
        const prefetched = getPrefetchedMapData();
        if (prefetched) {
            if (prefetched.mapgen4Param) {
                this.param = mergeMapgen4Param(prefetched.mapgen4Param);
                Logger.info(
                    '[WorldManager] Using mapgen4Param from map data, meshSeed:', this.param.meshSeed,
                    'towns:', this.param.towns?.numTowns ?? '?',
                    'railroads:', this.param.railroads?.enabled ?? false
                );
            }
            this._manualData = {
                manualTowns: prefetched.manualTowns,
                manualStations: prefetched.manualStations,
                railroadWaypoints: prefetched.railroadWaypoints
            };
        } else {
            this.param = DEFAULT_MAPGEN4_PARAM;
            Logger.info('[WorldManager] No mapgen4Param in prefetched data, using DEFAULT_MAPGEN4_PARAM, meshSeed:', this.param.meshSeed);
        }
        this.buildMeshAndCacheTownsRoads();
    }

    getWorldSize(): { width: number; height: number } {
        return { width: WORLD_SIZE, height: WORLD_SIZE };
    }

    getHeroSpawnPosition(): { x: number; y: number } {
        if (this._heroSpawn) return this._heroSpawn;
        return { x: WORLD_SIZE / 2, y: WORLD_SIZE / 2 };
    }

    /** Set hero spawn from map data. Called when map is loaded or hero spawn is moved in editor. */
    setHeroSpawn(x: number, y: number): void {
        this._heroSpawn = { x, y };
    }

    /** Clear hero spawn (revert to map center). Called when loading a map with no hero spawn. */
    clearHeroSpawn(): void {
        this._heroSpawn = null;
    }

    getMesh(): MeshAndMap | null {
        return this.meshAndMap;
    }

    getMapgen4Param(): Mapgen4Param {
        return this.param;
    }

    /** Apply mapgen4 params and manual data from loaded map, then rebuild mesh. */
    setMapgen4ParamAndRebuild(param: Mapgen4Param, manualData?: ManualTownsAndRailroads): void {
        this.param = mergeMapgen4Param(param);
        if (manualData) {
            this._manualData = manualData;
        }
        this.buildMeshAndCacheTownsRoads();
        Logger.info('[WorldManager] Mesh rebuilt with map params, meshSeed:', param.meshSeed);
    }

    /** Cached towns, roads, and railroads for polygon map rendering (avoids recompute every frame). */
    getCachedTownsAndRoads(): {
        towns: TownSite[];
        roadSegments: RoadSegment[];
        railroadPath: number[];
        railroadCrossings: RailroadCrossing[];
        railroadStationIds: number[];
    } {
        return {
            towns: this._cachedTowns,
            roadSegments: this._cachedRoadSegments,
            railroadPath: this._cachedRailroadPath,
            railroadCrossings: this._cachedRailroadCrossings,
            railroadStationIds: this._cachedRailroadStationIds
        };
    }

    /** World coords  mesh coords (0..1000) */
    worldToMesh(x: number, y: number): { x: number; y: number } {
        return { x: (x / WORLD_SIZE) * MESH_SIZE, y: (y / WORLD_SIZE) * MESH_SIZE };
    }

    /** Mesh coords (0..1000)  world coords */
    meshToWorld(mx: number, my: number): { x: number; y: number } {
        return { x: (mx / MESH_SIZE) * WORLD_SIZE, y: (my / MESH_SIZE) * WORLD_SIZE };
    }

    // --- Stub: walk everywhere ---
    isWalkable(_x: number, _y: number): boolean {
        return true;
    }

    isBlocked(_x: number, _y: number): boolean {
        return false;
    }
}

const WorldManager = new WorldManagerService();
if (Registry) Registry.register('WorldManager', WorldManager);

export { WorldManager, WorldManagerService };
